/**
 * BULLETPROOF ENTERPRISE SERVER
 * Properly configured Express server with guaranteed API routing
 */

import express from 'express';
import path from 'path';
import { fileURLToPath } from 'url';
import cors from 'cors';
import helmet from 'helmet';
import compression from 'compression';
import fs from 'fs';

// ES module compatibility
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const app = express();
const PORT = process.env.PORT || 10000;

// Logging middleware
const logger = (req, res, next) => {
  console.log(`[${new Date().toISOString()}] ${req.method} ${req.url}`);
  next();
};

// Security middleware
app.use(helmet({
  contentSecurityPolicy: {
    directives: {
      defaultSrc: ["'self'"],
      scriptSrc: [
        "'self'",
        "'unsafe-inline'",
        "'unsafe-eval'",
        "https://clerk.financeflo.ai",
        "https://*.clerk.accounts.dev",
        "https://*.clerk.com"
      ],
      styleSrc: [
        "'self'",
        "'unsafe-inline'",
        "https://fonts.googleapis.com"
      ],
      fontSrc: ["'self'", "https://fonts.gstatic.com"],
      imgSrc: ["'self'", "data:", "https:"],
      connectSrc: [
        "'self'",
        "https://clerk.financeflo.ai",
        "https://*.clerk.accounts.dev",
        "https://*.clerk.com",
        "https://mcp-server-tkyu.onrender.com"
      ]
    }
  }
}));

// Standard middleware
app.use(compression());
app.use(cors());
app.use(express.json({ limit: '50mb' }));
app.use(express.urlencoded({ extended: true, limit: '50mb' }));
app.use(logger);

// ==========================================
// API ROUTES (HIGHEST PRIORITY)
// ==========================================

// Health check endpoint
app.get('/health', (req, res) => {
  res.json({
    status: 'healthy',
    service: 'sentia-manufacturing-dashboard',
    version: '2.0.0-bulletproof',
    environment: process.env.NODE_ENV || 'development',
    timestamp: new Date().toISOString(),
    uptime: process.uptime(),
    clerk: {
      configured: Boolean(process.env.VITE_CLERK_PUBLISHABLE_KEY),
      publishableKey: process.env.VITE_CLERK_PUBLISHABLE_KEY ? 'SET' : 'NOT_SET'
    }
  });
});

// API Status endpoint
app.get('/api/status', (req, res) => {
  res.json({
    success: true,
    data: {
      service: 'sentia-manufacturing-dashboard',
      version: '2.0.0-bulletproof',
      environment: process.env.NODE_ENV || 'development',
      timestamp: new Date().toISOString(),
      uptime: process.uptime(),
      memory: process.memoryUsage(),
      clerk: {
        configured: Boolean(process.env.VITE_CLERK_PUBLISHABLE_KEY)
      }
    },
    meta: {
      timestamp: new Date().toISOString(),
      request_id: Math.random().toString(36).substr(2, 9)
    }
  });
});

// Dashboard Summary endpoint
app.get('/api/dashboard/summary', (req, res) => {
