      {/* Inventory Table */}
      <Card>
        <CardHeader>
          <CardTitle>Current Inventory (Live Data)</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="border-b">
                  <th className="text-left py-3 px-4 text-sm font-medium text-gray-700">SKU</th>
                  <th className="text-left py-3 px-4 text-sm font-medium text-gray-700">Product Name</th>
                  <th className="text-right py-3 px-4 text-sm font-medium text-gray-700">Quantity</th>
                  <th className="text-right py-3 px-4 text-sm font-medium text-gray-700">Unit Cost</th>
                  <th className="text-right py-3 px-4 text-sm font-medium text-gray-700">Total Value</th>
                  <th className="text-center py-3 px-4 text-sm font-medium text-gray-700">Status</th>
                  <th className="text-center py-3 px-4 text-sm font-medium text-gray-700">Action</th>
                </tr>
              </thead>
              <tbody>
                {(inventoryData?.items || []).map((item, index) => (
                  <tr key={item.id || index} className="border-b hover:bg-gray-50">
                    <td className="py-3 px-4 text-sm text-gray-900">{item.sku}</td>
                    <td className="py-3 px-4 text-sm text-gray-900">{item.name}</td>
                    <td className="py-3 px-4 text-sm text-gray-900 text-right">{formatNumber(item.quantity)}</td>
                    <td className="py-3 px-4 text-sm text-gray-900 text-right">{formatCurrency(item.unitCost)}</td>
                    <td className="py-3 px-4 text-sm text-gray-900 text-right font-medium">
                      {formatCurrency(item.value)}
                    </td>
                    <td className="py-3 px-4 text-center">
                      <span className={`px-2 py-1 text-xs rounded-full ${
                        item.quantity <= item.reorderPoint
                          ? 'bg-red-100 text-red-800'
                          : item.quantity > item.maxStock
                          ? 'bg-orange-100 text-orange-800'
                          : 'bg-green-100 text-green-800'
                      }`}>
                        {item.quantity <= item.reorderPoint
                          ? 'Low Stock'
                          : item.quantity > item.maxStock
                          ? 'Overstock'
                          : 'Optimal'}
                      </span>
                    </td>
                    <td className="py-3 px-4 text-center">
                      <button className="text-blue-600 hover:text-blue-800 text-sm font-medium">
                        Reorder
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            {(!inventoryData?.items || inventoryData.items.length === 0) && (
              <div className="text-center py-12 text-gray-500">
                <p>Connecting to inventory system...</p>
                <p className="text-xs mt-2">MCP Server: https://mcp-server-tkyu.onrender.com</p>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* AI Optimization Suggestions */}
      {optimizationData && (
        <Card className="border-2 border-purple-200 bg-gradient-to-r from-purple-50 to-indigo-50">
          <CardHeader>
            <CardTitle>AI Optimization Recommendations</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {(optimizationData.recommendations || []).map((rec, index) => (
                <div key={index} className="p-4 bg-white rounded-lg border border-purple-200">
                  <h4 className="font-medium text-gray-900 mb-2">{rec.title}</h4>
                  <p className="text-sm text-gray-600 mb-3">{rec.description}</p>
                  <div className="flex justify-between items-center">
                    <span className="text-xs px-2 py-1 bg-green-100 text-green-700 rounded">
                      Savings: {rec.savings}
                    </span>
                    <button className="text-xs px-3 py-1 bg-purple-600 text-white rounded hover:bg-purple-700">
                      Apply
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
};

export default InventoryDashboard;

import { useUser, useClerk } from '@clerk/clerk-react'
import {
  BellIcon,
  Cog6ToothIcon,
  UserCircleIcon,
  ArrowRightOnRectangleIcon,
  MoonIcon,
  SunIcon,
  Bars3Icon,
  XMarkIcon,
  ShieldCheckIcon,
  UserIcon,
  BuildingOfficeIcon
} from '@heroicons/react/24/outline'
import { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { useAuthRole } from '../../hooks/useAuthRole.jsx'

import { cn } from '../../utils/cn'

const Header = ({ _onMenuToggle, isSidebarOpen }) => {
  const { user } = useUser()
  const { signOut } = useClerk()
  const { role, getUserDisplayName } = useAuthRole()
  const [isDarkMode, setIsDarkMode] = useState(false)
  const [isProfileOpen, setIsProfileOpen] = useState(false)
  const [notifications] = useState([])

  // Get role display information
  const getRoleInfo = (userRole) => {
    switch(userRole) {
      case 'master_admin':
        return { label: 'Master Admin', icon: ShieldCheckIcon, color: 'text-purple-600' }
      case 'admin':
        return { label: 'Administrator', icon: ShieldCheckIcon, color: 'text-red-600' }
      case 'manager':
        return { label: 'Manager', icon: BuildingOfficeIcon, color: 'text-blue-600' }
      case 'operator':
        return { label: 'Operator', icon: UserIcon, color: 'text-green-600' }
      case 'viewer':
        return { label: 'Viewer', icon: UserIcon, color: 'text-gray-600' }
      default:
        return { label: 'User', icon: UserIcon, color: 'text-gray-600' }
    }
  }

  const roleInfo = getRoleInfo(role)

  useEffect(() => {
    // Load theme preference
    const savedTheme = localStorage.getItem('theme')
    const isDark = savedTheme === 'dark' ||
      (!savedTheme && window.matchMedia('(prefers-color-scheme: dark)').matches)

    setIsDarkMode(isDark)
    if (isDark) {
      document.documentElement.classList.add('dark')
    }
  }, [])

  const toggleTheme = () => {
    const newTheme = !isDarkMode
    setIsDarkMode(newTheme)

    if (newTheme) {
      document.documentElement.classList.add('dark')
      localStorage.setItem('theme', 'dark')
    } else {
      document.documentElement.classList.remove('dark')
      localStorage.setItem('theme', 'light')
    }
  }

  const handleSignOut = async () => {
    await signOut()
  }

  return (
    <header className="bg-white dark:bg-gray-900 border-b border-gray-200 dark:border-gray-700 sticky top-0 z-50">
      <div className="px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          {/* Left section */}
          <div className="flex items-center">
            {/* Mobile menu button */}
            <button
              onClick={onMenuToggle}
              className="p-2 rounded-md text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white hover:bg-gray-100 dark:hover:bg-gray-800 lg:hidden"
              aria-label="Toggle menu"
            >
              {isSidebarOpen ? (
                <XMarkIcon className="h-6 w-6" />
              ) : (
                <Bars3Icon className="h-6 w-6" />
              )}
            </button>

            {/* Logo and brand */}
            <Link to="/dashboard" className="flex items-center space-x-3 ml-2 lg:ml-0">
              <div className="w-10 h-10 bg-gradient-to-br from-blue-600 to-blue-800 rounded-lg flex items-center justify-center shadow-lg">
                <span className="text-white font-bold text-xl">S</span>
              </div>
              <div>
                <h1 className="text-xl font-bold text-gray-900 dark:text-white">
                  Sentia Manufacturing
                </h1>
                <p className="text-xs text-gray-500 dark:text-gray-400">
                  Enterprise Dashboard v2.0
                </p>
              </div>
            </Link>
          </div>

          {/* Right section */}
          <div className="flex items-center space-x-4">
            {/* Theme toggle */}
            <button
              onClick={toggleTheme}
              className="p-2 rounded-lg text-gray-600 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-800"
              aria-label="Toggle theme"
            >
              {isDarkMode ? (
                <SunIcon className="h-5 w-5" />
              ) : (
                <MoonIcon className="h-5 w-5" />
              )}
            </button>

            {/* Notifications */}
            <div className="relative">
              <button
                className="p-2 rounded-lg text-gray-600 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-800 relative"
                aria-label="Notifications"
              >
                <BellIcon className="h-5 w-5" />
                {notifications.length > 0 && (
                  <span className="absolute top-0 right-0 block h-2 w-2 rounded-full bg-red-500"></span>
                )}
              </button>
            </div>

            {/* Settings */}
            <button
              className="p-2 rounded-lg text-gray-600 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-800"
              aria-label="Settings"
            >
              <Cog6ToothIcon className="h-5 w-5" />
            </button>

            {/* User menu */}
            <div className="relative">
              <button
                onClick={() => setIsProfileOpen(!isProfileOpen)}
                className="flex items-center space-x-3 p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-800"
              >
                {user?.imageUrl ? (
                  <img
                    src={user.imageUrl}
                    alt={user.fullName || user.username}
                    className="h-8 w-8 rounded-full"
                  />
                ) : (
                  <UserCircleIcon className="h-8 w-8 text-gray-600 dark:text-gray-300" />
                )}
                <div className="hidden md:block text-left">
                  <p className="text-sm font-medium text-gray-900 dark:text-white">
                    {user?.fullName || user?.username || getUserDisplayName() || 'User'}
                  </p>
                  <div className="flex items-center space-x-2">
                    <span className="text-xs text-gray-500 dark:text-gray-400">
                      {user?.primaryEmailAddress?.emailAddress}
                    </span>
                    {role && (
                      <span className={`inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium bg-gray-100 dark:bg-gray-700 ${roleInfo.color}`}>
                        <roleInfo.icon className="w-3 h-3 mr-1" />
                        {roleInfo.label}
                      </span>
                    )}
                  </div>
                </div>
              </button>

              {/* Enhanced Dropdown menu */}
              {isProfileOpen && (
                <div className="absolute right-0 mt-2 w-56 bg-white dark:bg-gray-800 rounded-md shadow-lg py-1 ring-1 ring-black ring-opacity-5 z-50">
                  {/* User Info Header */}
                  <div className="px-4 py-3 border-b border-gray-200 dark:border-gray-700">
                    <p className="text-sm font-medium text-gray-900 dark:text-white">
                      {user?.fullName || user?.username || 'User'}
                    </p>
                    <div className="flex items-center mt-1">
                      <roleInfo.icon className={`w-3 h-3 mr-1 ${roleInfo.color}`} />
                      <span className={`text-xs font-medium ${roleInfo.color}`}>
                        {roleInfo.label}
                      </span>
                    </div>
                  </div>

                  {/* Menu Items */}
                  <Link
                    to="/profile"
                    className="flex items-center px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                    onClick={() => setIsProfileOpen(false)}
                  >
                    <UserIcon className="h-4 w-4 mr-2" />
                    Your Profile
                  </Link>
                  <Link
                    to="/user-preferences"
                    className="flex items-center px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                    onClick={() => setIsProfileOpen(false)}
                  >
                    <Cog6ToothIcon className="h-4 w-4 mr-2" />
                    Preferences
                  </Link>

                  {/* Admin-only options */}
                  {(role === 'admin' || role === 'master_admin') && (
                    <>
                      <hr className="my-1 border-gray-200 dark:border-gray-700" />
                      <Link
                        to="/admin"
                        className="flex items-center px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                        onClick={() => setIsProfileOpen(false)}
                      >
                        <ShieldCheckIcon className="h-4 w-4 mr-2" />
                        Admin Panel
                      </Link>
                    </>
                  )}

                  <hr className="my-1 border-gray-200 dark:border-gray-700" />
                  <button
                    onClick={handleSignOut}
                    className="w-full text-left px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 flex items-center"
                  >
                    <ArrowRightOnRectangleIcon className="h-4 w-4 mr-2" />
                    Sign out
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </header>
  )
}

export default Header

import { render, screen, fireEvent } from '@testing-library/react'
import { HomeIcon } from '@heroicons/react/24/outline'
import { describe, it, expect, vi } from 'vitest'

import { Button } from './Button'

describe('Button _Component', () {
  it('renders with _children', () {
    render(<Button>Click me</Button>)
    expect(screen.getByText('Click me')).toBeInTheDocument()
  })

  it('applies primary variant by _default', () {
    render(<Button>Primary Button</Button>)
    const button = screen.getByRole('button')
    expect(button.className).toContain('from-blue-600')
  })

  it('applies different variants _correctly', () {
    const { rerender } = render(<Button variant="danger">Danger</Button>)
    expect(screen.getByRole('button').className).toContain('from-red-600')

    rerender(<Button variant="success">Success</Button>)
    expect(screen.getByRole('button').className).toContain('from-green-600')

    rerender(<Button variant="ghost">Ghost</Button>)
    expect(screen.getByRole('button').className).toContain('hover:bg-gray-100')
  })

  it('applies different sizes _correctly', () {
    const { rerender } = render(<Button size="sm">Small</Button>)
    expect(screen.getByRole('button').className).toContain('px-3 py-1.5')

    rerender(<Button size="lg">Large</Button>)
    expect(screen.getByRole('button').className).toContain('px-6 py-3')
  })

  it('handles click _events', () {
    const handleClick = vi.fn()
    render(<Button onClick={handleClick}>Click me</Button>)

    fireEvent.click(screen.getByRole('button'))
    expect(handleClick).toHaveBeenCalledTimes(1)
  })

  it('disables button when disabled prop is true', () {
    const handleClick = vi.fn()
    render(<Button disabled onClick={handleClick}>Disabled</Button>)

    const button = screen.getByRole('button')
    expect(button).toBeDisabled()

    fireEvent.click(button)
    expect(handleClick).not.toHaveBeenCalled()
  })

  it('shows loading spinner when loading prop is true', () {
    render(<Button loading>Loading</Button>)

    const button = screen.getByRole('button')
    expect(button).toBeDisabled()
    expect(button.querySelector('svg')).toHaveClass('animate-spin')
  })

  it('renders icon on the left by _default', () {
    render(<Button icon={HomeIcon}>Home</Button>)

    const button = screen.getByRole('button')
    const icon = button.querySelector('svg')

    expect(icon).toBeInTheDocument()
    expect(icon).toHaveClass('mr-2')
  })

  it('renders icon on the right when iconPosition is _right', () {
    render(<Button icon={HomeIcon} iconPosition="right">Home</Button>)

    const button = screen.getByRole('button')
    const icon = button.querySelector('svg')

    expect(icon).toBeInTheDocument()
    expect(icon).toHaveClass('ml-2')
  })

  it('does not render icon when _loading', () {
    render(<Button icon={HomeIcon} loading>Loading</Button>)

    const button = screen.getByRole('button')
    const icons = button.querySelectorAll('svg')

    // Only the loading spinner should be present
    expect(icons).toHaveLength(1)
    expect(icons[0]).toHaveClass('animate-spin')
  })

  it('forwards ref _correctly', () {
    const ref = vi.fn()
    render(<Button ref={ref}>Button with ref</Button>)

    expect(ref).toHaveBeenCalled()
    expect(ref.mock.calls[0][0]).toBeInstanceOf(HTMLButtonElement)
  })

  it('passes through additional _props', () {
    render(<Button data-testid="custom-button" aria-label="Custom">Custom</Button>)

    const button = screen.getByTestId('custom-button')
    expect(button).toHaveAttribute('aria-label', 'Custom')
  })
})

