export const useExecutiveMetrics = (period = 'month') => {
  const [metrics, setMetrics] = useState(null)
  const [alerts, setAlerts] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)

  useEffect(() => {
    let isMounted = true

    const loadMetrics = async () => {
      setLoading(true)
      setError(null)

      try {
        const data = await fetchExecutiveMetrics(period)

        if (!isMounted) return

        setMetrics(data)

        // Generate alerts based on metrics
        const newAlerts = []

        // Check for critical KPIs
        data.kpis?.forEach(kpi => {
          if (kpi.status === 'critical') {
            newAlerts.push({
              id: `kpi-${kpi.id}`,
              severity: 'critical',
              message: `${kpi.name} is below critical threshold (${kpi.value}${kpi.unit} vs target ${kpi.target}${kpi.unit})`
            })
          } else if (kpi.status === 'warning') {
            newAlerts.push({
              id: `kpi-${kpi.id}`,
              severity: 'warning',
              message: `${kpi.name} needs attention (${kpi.value}${kpi.unit} vs target ${kpi.target}${kpi.unit})`
            })
          }
        })

        // Check for high-impact risks
        data.risks?.forEach((risk, _index) => {
          if (risk.impact === 'High' && risk.probability !== 'Low') {
            newAlerts.push({
              id: `risk-${index}`,
              severity: 'critical',
              message: risk.title
            })
          }
        })

        setAlerts(newAlerts)
      } catch (err) {
        if (!isMounted) return

        setError(err)
        logError('Error loading executive metrics', err)
      } finally {
        if (isMounted) {
          setLoading(false)
        }
      }
    }

    loadMetrics()

    // Set up polling for real-time updates
    const interval = setInterval(loadMetrics, 30000) // Update every 30 seconds

    return () => {
      isMounted = false
      clearInterval(interval)
    }
  }, [period])

  return {
    metrics,
    alerts,
    loading,
    error,
    refresh: () => {
      setLoading(true)
      fetchExecutiveMetrics(period).then(data => {
        setMetrics(data)
        setLoading(false)
      }).catch(err => {
        setError(err)
        setLoading(false)
      })
    }
  }
}

