import { useMemo } from 'react'

import { Card, CardContent, CardHeader, CardTitle , Badge } from '../../../components/ui'
import { cn } from '../../../utils/cn'

const generateMockInventory = () => {
  const skus = ['SNTG-001', 'SNTG-002', 'SNTB-001', 'SNTB-002', 'SNTR-001', 'SNTR-002', 'SNTG-003', 'SNTB-003', 'SNTR-003']
  const locations = ['UK-London', 'UK-Manchester', 'EU-Amsterdam', 'EU-Berlin', 'US-NYC', 'US-LA', 'US-Chicago']

  return skus.map(sku => ({
    sku,
    name: `Sentia ${sku.includes('G') ? 'Ginger' : sku.includes('B') ? 'Black' : 'Red'} ${sku.slice(-3)}`,
    locations: locations.map(location => ({
      location,
      quantity: Math.floor(Math.random() * 5000),
      daysOfSupply: Math.floor(Math.random() * 90),
      status: Math.random() > 0.7 ? 'critical' : Math.random() > 0.4 ? 'warning' : 'healthy'
    }))
  }))
}

export function InventoryHeatmap({ data, onCellClick }) {
  const inventoryData = useMemo(() => {
    return data || generateMockInventory()
  }, [data])

  const locations = useMemo(() => {
    if (inventoryData.length > 0) {
      return inventoryData[0].locations.map(l => l.location)
    }
    return []
  }, [inventoryData])

  const getHeatmapColor = (status, quantity) => {
    if (status === 'critical') return 'bg-red-500'
    if (status === 'warning') return 'bg-amber-500'
    if (quantity > 3000) return 'bg-green-600'
    if (quantity > 1500) return 'bg-green-500'
    if (quantity > 500) return 'bg-green-400'
    return 'bg-green-300'
  }

  const getTextColor = (status, quantity) => {
    if (status === 'critical' || status === 'warning' || quantity > 1500) {
      return 'text-white'
    }
    return 'text-gray-900'
  }

  const formatQuantity = (qty) => {
    if (qty >= 1000) {
      return `${(qty / 1000).toFixed(1)}k`
    }
    return qty.toString()
  }

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle>Inventory Distribution Heatmap</CardTitle>
          <div className="flex gap-2">
            <Badge variant="success">Healthy</Badge>
            <Badge variant="warning">Low Stock</Badge>
            <Badge variant="destructive">Critical</Badge>
          </div>
        </div>
      </CardHeader>
      <CardContent>
        <div className="overflow-x-auto">
          <div className="min-w-[800px]">
            {/* Header Row */}
            <div className="grid grid-cols-10 gap-1 mb-2">
              <div className="col-span-2 px-2 py-1 text-sm font-semibold">SKU</div>
              {locations.map(location => (
                <div key={location} className="px-1 py-1 text-xs font-medium text-center truncate">
                  {location}
                </div>
              ))}
              <div className="px-2 py-1 text-sm font-semibold text-center">Total</div>
            </div>

            {/* Data Rows */}
            {inventoryData.map((item, index) => {
              const totalQuantity = item.locations.reduce((sum, loc) => sum + loc.quantity, 0)

              return (
                <div key={item.sku} className={cn(
                  "grid grid-cols-10 gap-1 mb-1",
                  index % 2 === 0 ? 'bg-gray-50 dark:bg-gray-900' : ''
                )}>
                  <div className="col-span-2 px-2 py-2">
                    <div className="text-sm font-medium">{item.sku}</div>
                    <div className="text-xs text-gray-600 truncate">{item.name}</div>
                  </div>

                  {item.locations.map(loc => (
                    <button
                      key={`${item.sku}-${loc.location}`}
                      onClick={() => onCellClick && onCellClick(item.sku, loc)}
                      className={cn(
                        "px-1 py-2 rounded text-xs font-semibold transition-all hover:opacity-90",
                        getHeatmapColor(loc.status, loc.quantity),
                        getTextColor(loc.status, loc.quantity)
                      )}
                    >
                      <div>{formatQuantity(loc.quantity)}</div>
                      <div className="text-[10px] opacity-90">{loc.daysOfSupply}d</div>
                    </button>
                  ))}

                  <div className="px-2 py-2 text-center">
                    <div className="text-sm font-bold">{formatQuantity(totalQuantity)}</div>
                    <div className="text-xs text-gray-600">units</div>
                  </div>
                </div>
              )
            })}

            {/* Summary Row */}
            <div className="grid grid-cols-10 gap-1 mt-2 pt-2 border-t">
              <div className="col-span-2 px-2 py-1 text-sm font-semibold">Location Total</div>
              {locations.map(location => {
                const locationTotal = _inventoryData.reduce((sum, item) => {
                  const loc = item.locations.find(l => l.location === location)
                  return sum + (loc?.quantity || 0)
                }, 0)

                return (
                  <div key={location} className="px-1 py-1 text-xs font-bold text-center">
                    {formatQuantity(locationTotal)}
                  </div>
                )
              })}
              <div className="px-2 py-1 text-sm font-bold text-center">
                {formatQuantity(inventoryData.reduce((sum, item) =>
                  sum + item.locations.reduce((locSum, loc) => locSum + loc.quantity, 0), 0
                ))}
              </div>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  )
}

import React, { useState } from 'react'
import { ChartBarIcon, ArrowTrendingUpIcon, ArrowTrendingDownIcon } from '@heroicons/react/24/solid'

export default function StockMovementForecast({ data, period = 'current' }) {
  const [forecastPeriod, setForecastPeriod] = useState('30')

  // Mock stock movement forecast data if not provided
  const generateForecastData = (days) => {
    const forecast = []
    const today = new Date()

    // Mock SKUs with different movement patterns
    const skus = [
      { sku: 'SNTG-001', baseDaily: 45, pattern: 'steady', seasonal: 1.0 },
      { sku: 'SNTB-001', baseDaily: 32, pattern: 'growing', seasonal: 1.1 },
      { sku: 'SNTR-001', baseDaily: 28, pattern: 'declining', seasonal: 0.9 },
      { sku: 'SNTG-002', baseDaily: 38, pattern: 'volatile', seasonal: 1.2 }
    ]

    for (let day = 0; day < parseInt(days); day++) {
      const date = new Date(today)
      date.setDate(date.getDate() + day)

      skus.forEach(sku => {
        // Calculate daily movement with pattern and seasonality
        let movement = sku.baseDaily * sku.seasonal

        // Apply pattern
        switch (sku.pattern) {
          case 'growing':
            movement *= 1 + (day * 0.005) // 0.5% daily growth
            break
          case 'declining':
            movement *= 1 - (day * 0.003) // 0.3% daily decline
            break
          case 'volatile':
            movement *= 1 + (Math.sin(day / 7) * 0.3) // Weekly volatility
            break
          case 'steady':
          default:
            movement *= 1 + ((Math.random() - 0.5) * 0.1) // Â±5% variance
            break
        }

        // Weekend effect (reduced movement)
        const dayOfWeek = date.getDay()
        if (dayOfWeek === 0 || dayOfWeek === 6) {
          movement *= 0.3
        }

        forecast.push({
          date: date.toISOString().split('T')[0],
          dateFormatted: date.toLocaleDateString(),
          sku: sku.sku,
          predictedMovement: Math.round(Math.max(0, movement)),
          confidence: Math.random() * 0.2 + 0.8, // 80-100% confidence
          pattern: sku.pattern
        })
      })
    }

    return forecast
  }

  const forecastData = data || generateForecastData(forecastPeriod)

  // Aggregate by date for summary view
  const dailyTotals = forecastData.reduce((acc, item) => {
    if (!acc[item.date]) {
      acc[item.date] = {
        date: item.date,
        dateFormatted: item.dateFormatted,
        totalMovement: 0,
        avgConfidence: 0,
        itemCount: 0
      }
    }
    acc[item.date].totalMovement += item.predictedMovement
    acc[item.date].avgConfidence += item.confidence
    acc[item.date].itemCount += 1
    return acc
  }, {})

  // Calculate average confidence
  Object.values(dailyTotals).forEach(day => {
    day.avgConfidence = day.avgConfidence / day.itemCount
  })

  const sortedDailyTotals = Object.values(dailyTotals).sort((a, b) =>
    new Date(a.date) - new Date(b.date)
  )

  // Calculate trend
  const totalMovement = sortedDailyTotals.reduce((sum, day) => sum + day.totalMovement, 0)
  const avgDailyMovement = totalMovement / sortedDailyTotals.length

  const firstWeekAvg = sortedDailyTotals.slice(0, 7).reduce((sum, day) => sum + day.totalMovement, 0) / 7
  const lastWeekAvg = sortedDailyTotals.slice(-7).reduce((sum, day) => sum + day.totalMovement, 0) / 7
  const trend = ((lastWeekAvg - firstWeekAvg) / firstWeekAvg) * 100

  // Get top moving SKUs
  const skuTotals = forecastData.reduce((acc, item) => {
    if (!acc[item.sku]) {
      acc[item.sku] = { sku: item.sku, totalMovement: 0, pattern: item.pattern }
    }
    acc[item.sku].totalMovement += item.predictedMovement
    return acc
  }, {})

  const topSkus = Object.values(skuTotals)
    .sort((a, b) => b.totalMovement - a.totalMovement)
    .slice(0, 4)

  const getPatternColor = (pattern) => {
    switch (pattern) {
      case 'growing':
        return 'text-green-600 dark:text-green-400'
      case 'declining':
        return 'text-red-600 dark:text-red-400'
      case 'volatile':
        return 'text-orange-600 dark:text-orange-400'
      case 'steady':
      default:
        return 'text-blue-600 dark:text-blue-400'
    }
  }

  const getPatternIcon = (pattern) => {
    switch (pattern) {
      case 'growing':
        return <ArrowTrendingUpIcon className="h-4 w-4 text-green-600 dark:text-green-400" />
      case 'declining':
        return <ArrowTrendingDownIcon className="h-4 w-4 text-red-600 dark:text-red-400" />
      default:
        return <ChartBarIcon className="h-4 w-4 text-blue-600 dark:text-blue-400" />
    }
  }

  return (
    <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-gray-900 dark:text-white flex items-center">
          <ChartBarIcon className="h-6 w-6 text-blue-600 dark:text-blue-400 mr-2" />
          Stock Movement Forecast
        </h3>

        {/* Period Selector */}
        <select
          value={forecastPeriod}
          onChange={(e) => setForecastPeriod(e.target.value)}
          className="px-3 py-1 text-sm border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
        >
          <option value="7">7 Days</option>
          <option value="14">14 Days</option>
          <option value="30">30 Days</option>
          <option value="60">60 Days</option>
        </select>
      </div>

      {/* Summary Metrics */}
      <div className="grid grid-cols-3 gap-4 mb-6">
        <div className="bg-blue-50 dark:bg-blue-900/20 rounded-lg p-3">
          <p className="text-xs text-blue-600 dark:text-blue-400">Avg Daily Movement</p>
          <p className="text-lg font-bold text-blue-900 dark:text-blue-100">
            {Math.round(avgDailyMovement)}
          </p>
          <p className="text-xs text-blue-600 dark:text-blue-400">units/day</p>
        </div>

        <div className={`rounded-lg p-3 ${
          trend >= 0
            ? 'bg-green-50 dark:bg-green-900/20'
            : 'bg-red-50 dark:bg-red-900/20'
        }`}>
          <p className={`text-xs ${
            trend >= 0
              ? 'text-green-600 dark:text-green-400'
              : 'text-red-600 dark:text-red-400'
          }`}>
            Trend
          </p>
          <p className={`text-lg font-bold ${
            trend >= 0
              ? 'text-green-900 dark:text-green-100'
              : 'text-red-900 dark:text-red-100'
          }`}>
            {trend >= 0 ? '+' : ''}{trend.toFixed(1)}%
          </p>
          <p className={`text-xs ${
            trend >= 0
              ? 'text-green-600 dark:text-green-400'
              : 'text-red-600 dark:text-red-400'
          }`}>
            vs first week
          </p>
        </div>

        <div className="bg-purple-50 dark:bg-purple-900/20 rounded-lg p-3">
          <p className="text-xs text-purple-600 dark:text-purple-400">Total Forecast</p>
          <p className="text-lg font-bold text-purple-900 dark:text-purple-100">
            {totalMovement.toLocaleString()}
          </p>
          <p className="text-xs text-purple-600 dark:text-purple-400">units</p>
        </div>
      </div>

      {/* Top SKUs */}
      <div className="mb-6">
        <h4 className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-3">
          Top Moving SKUs
        </h4>
        <div className="space-y-2">
          {topSkus.map((sku) => (
            <div key={sku.sku} className="flex items-center justify-between p-2 bg-gray-50 dark:bg-gray-900 rounded">
              <div className="flex items-center">
                {getPatternIcon(sku.pattern)}
                <span className="ml-2 text-sm font-medium text-gray-900 dark:text-white">
                  {sku.sku}
                </span>
                <span className={`ml-2 text-xs ${getPatternColor(sku.pattern)}`}>
                  {sku.pattern}
                </span>
              </div>
              <span className="text-sm text-gray-600 dark:text-gray-400">
                {sku.totalMovement.toLocaleString()} units
              </span>
            </div>
          ))}
        </div>
      </div>

      {/* Mini Chart */}
      <div className="mb-4">
        <h4 className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-3">
          Daily Movement Trend
        </h4>
        <div className="relative h-24 bg-gray-50 dark:bg-gray-900 rounded-lg p-2">
          <div className="flex h-full items-end justify-between">
            {sortedDailyTotals.slice(0, 14).map((day, index) => {
              const maxMovement = Math.max(...sortedDailyTotals.map(d => d.totalMovement))
              const height = (day.totalMovement / maxMovement) * 100

              return (
                <div key={day.date} className="flex flex-col items-center">
                  <div
                    className={`w-2 rounded-t transition-all duration-300 ${
                      day.avgConfidence > 0.9 ? 'bg-green-500' :
                      day.avgConfidence > 0.85 ? 'bg-yellow-500' : 'bg-red-500'
                    }`}
                    style={{ height: `${height}%` }}
                    title={`${day.dateFormatted}: ${day.totalMovement} units (${(day.avgConfidence * 100).toFixed(0)}% confidence)`}
                  />
                  {index % 2 === 0 && (
                    <span className="text-xs text-gray-500 dark:text-gray-400 mt-1 transform -rotate-45">
                      {new Date(day.date).toLocaleDateString('en', { month: 'short', day: 'numeric' })}
                    </span>
                  )}
                </div>
              )
            })}
          </div>
        </div>
      </div>

      {/* Confidence Legend */}
      <div className="flex items-center justify-center space-x-6 text-xs mb-4">
        <div className="flex items-center">
          <div className="w-3 h-3 bg-green-500 rounded mr-1"></div>
          <span className="text-gray-600 dark:text-gray-400">High Confidence (90%+)</span>
        </div>
        <div className="flex items-center">
          <div className="w-3 h-3 bg-yellow-500 rounded mr-1"></div>
          <span className="text-gray-600 dark:text-gray-400">Medium (85-90%)</span>
        </div>
        <div className="flex items-center">
          <div className="w-3 h-3 bg-red-500 rounded mr-1"></div>
          <span className="text-gray-600 dark:text-gray-400">Low (&lt;85%)</span>
        </div>
      </div>

      {/* Action Button */}
      <button className="w-full px-3 py-2 text-sm bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition">
        View Detailed Forecast
      </button>
    </div>
  )
}

import { useState, useMemo, memo } from 'react'
import {
  ChartBarIcon,
  ClockIcon,
  CogIcon,
  CheckCircleIcon,
  ArrowTrendingUpIcon,
  ArrowTrendingDownIcon,
  ExclamationTriangleIcon
} from '@heroicons/react/24/outline'
import { Card, CardContent, CardHeader, CardTitle } from '../../../components/ui'

const OEEDisplay = memo(function OEEDisplay({ data, timeRange = '24h' }) {
  const [selectedLine] = useState('all')

  // Mock data fallback
  const oeeData = useMemo(() => data || {
    overall: 75.5,
    availability: 87.2,
    performance: 82.1,
    quality: 95.8,
    target: 85,
    worldClass: 90,
    availabilityChange: 2.3,
    performanceChange: -1.2,
    qualityChange: 0.8,
    lineBreakdown: []
  }, [data])

  const getOEEStatus = useMemo(() => (oeeValue) => {
    if (oeeValue >= 85) return { color: 'green', status: 'Excellent', bgColor: 'bg-green-100', textColor: 'text-green-800' }
    if (oeeValue >= 70) return { color: 'blue', status: 'Good', bgColor: 'bg-blue-100', textColor: 'text-blue-800' }
    if (oeeValue >= 60) return { color: 'yellow', status: 'Fair', bgColor: 'bg-yellow-100', textColor: 'text-yellow-800' }
    return { color: 'red', status: 'Poor', bgColor: 'bg-red-100', textColor: 'text-red-800' }
  }, [])

  const statusData = useMemo(() => ({
    overall: getOEEStatus(oeeData.overall),
    availability: getOEEStatus(oeeData.availability),
    performance: getOEEStatus(oeeData.performance),
    quality: getOEEStatus(oeeData.quality)
  }), [oeeData, getOEEStatus])

  const formatChange = (change) => {
    if (!change) return null
    const isPositive = change >= 0
    return (
      <span className={`flex items-center ml-2 text-sm ${isPositive ? 'text-green-600' : 'text-red-600'}`}>
        {isPositive ? <ArrowTrendingUpIcon className="h-4 w-4 mr-1" /> : <ArrowTrendingDownIcon className="h-4 w-4 mr-1" />}
        {Math.abs(change).toFixed(1)}%
      </span>
    )
  }

  const CircularProgress = ({ value, size = 120, strokeWidth = 8, color = 'blue' }) => {
    const radius = (size - strokeWidth) / 2
    const circumference = radius * 2 * Math.PI
    const offset = circumference - (value / 100) * circumference

    const colorMap = {
      green: '#10B981',
      blue: '#3B82F6',
      yellow: '#F59E0B',
      red: '#EF4444'
    }

    return (
      <div className="relative inline-flex items-center justify-center">
        <svg width={size} height={size} className="transform -rotate-90">
          <circle
            cx={size / 2}
            cy={size / 2}
            r={radius}
            stroke="#E5E7EB"
            strokeWidth={strokeWidth}
            fill="transparent"
          />
          <circle
            cx={size / 2}
            cy={size / 2}
            r={radius}
            stroke={colorMap[color]}
            strokeWidth={strokeWidth}
            fill="transparent"
            strokeDasharray={circumference}
            strokeDashoffset={offset}
            strokeLinecap="round"
            className="transition-all duration-1000 ease-out"
          />
        </svg>
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="text-center">
            <div className="text-2xl font-bold text-gray-900 dark:text-white">
              {value.toFixed(1)}%
            </div>
            <div className="text-xs text-gray-500">
              OEE
            </div>
          </div>
        </div>
      </div>
    )
  }

  const ComponentBar = ({ label, value, target, icon: Icon, color, change }) => {
    const percentage = Math.min((value / target) * 100, 100)
    const status = getOEEStatus(value)

    return (
      <div className="bg-white dark:bg-gray-800 p-4 rounded-lg border border-gray-200 dark:border-gray-700">
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center">
            <div className={`p-2 rounded-lg bg-${status.color}-100 dark:bg-${status.color}-900/20`}>
              <Icon className={`h-5 w-5 text-${status.color}-600 dark:text-${status.color}-400`} />
            </div>
            <div className="ml-3">
              <h4 className="text-sm font-medium text-gray-900 dark:text-white">{label}</h4>
              <div className="flex items-center">
                <span className="text-lg font-semibold text-gray-900 dark:text-white">
                  {value.toFixed(1)}%
                </span>
                {formatChange(change)}
              </div>
            </div>
          </div>
          <div className={`px-2 py-1 rounded-full text-xs font-medium ${status.bgColor} ${status.textColor}`}>
            {status.status}
          </div>
        </div>

        <div className="space-y-2">
          <div className="flex justify-between text-xs text-gray-600 dark:text-gray-400">
            <span>Progress to Target ({target}%)</span>
            <span>{percentage.toFixed(0)}%</span>
          </div>
          <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
            <div
              className={`h-2 rounded-full transition-all duration-1000 bg-${status.color}-500`}
              style={{ width: `${Math.min(percentage, 100)}%` }}
            />
          </div>
          {percentage > 100 && (
            <div className="flex items-center text-xs text-green-600">
              <ArrowTrendingUpIcon className="h-3 w-3 mr-1" />
              Exceeding target by {(percentage - 100).toFixed(1)}%
            </div>
          )}
        </div>
      </div>
    )
  }

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center">
            <ChartBarIcon className="h-6 w-6 mr-2 text-blue-600" />
            Overall Equipment Effectiveness (OEE)
          </CardTitle>
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600 dark:text-gray-400">
              Target: {oeeData.target}% | World Class: {oeeData.worldClass}%
            </span>
          </div>
        </div>
      </CardHeader>

      <CardContent>
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Overall OEE Circle */}
          <div className="flex flex-col items-center justify-center p-6">
            <CircularProgress
              value={oeeData.overall}
              color={statusData.overall.color}
              size={160}
              strokeWidth={12}
            />
            <div className="mt-4 text-center">
              <div className={`inline-flex px-3 py-1 rounded-full text-sm font-medium ${statusData.overall.bgColor} ${statusData.overall.textColor}`}>
                {statusData.overall.status}
              </div>
              <p className="text-xs text-gray-600 dark:text-gray-400 mt-2">
                Last updated: {new Date().toLocaleTimeString()}
              </p>
            </div>
          </div>

          {/* OEE Components */}
          <div className="space-y-4">
            <ComponentBar
              label="Availability"
              value={oeeData.availability}
              target={90}
              icon={ClockIcon}
              color={statusData.availability.color}
              change={oeeData.availabilityChange}
            />

            <ComponentBar
              label="Performance"
              value={oeeData.performance}
              target={95}
              icon={CogIcon}
              color={statusData.performance.color}
              change={oeeData.performanceChange}
            />

            <ComponentBar
              label="Quality"
              value={oeeData.quality}
              target={99}
              icon={CheckCircleIcon}
              color={statusData.quality.color}
              change={oeeData.qualityChange}
            />
          </div>
        </div>

        {/* OEE Formula */}
        <div className="mt-6 p-4 bg-gray-50 dark:bg-gray-800/50 rounded-lg">
          <h4 className="text-sm font-medium text-gray-900 dark:text-white mb-3">OEE Calculation</h4>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-center">
            <div className="flex items-center justify-center">
              <span className="text-lg font-mono">
                {oeeData.availability.toFixed(1)}%
              </span>
              <span className="mx-2 text-gray-400">Ã—</span>
            </div>
            <div className="flex items-center justify-center">
              <span className="text-lg font-mono">
                {oeeData.performance.toFixed(1)}%
              </span>
              <span className="mx-2 text-gray-400">Ã—</span>
            </div>
            <div className="flex items-center justify-center">
              <span className="text-lg font-mono">
                {oeeData.quality.toFixed(1)}%
              </span>
              <span className="mx-2 text-gray-400">=</span>
            </div>
          </div>
          <div className="text-center mt-2">
            <span className="text-xl font-mono font-bold text-blue-600">
              {oeeData.overall.toFixed(1)}% OEE
            </span>
          </div>
        </div>

        {/* Line Breakdown (if available) */}
        {oeeData.lineBreakdown && oeeData.lineBreakdown.length > 0 && (
          <div className="mt-6">
            <h4 className="text-sm font-medium text-gray-900 dark:text-white mb-3">OEE by Production Line</h4>
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
                <thead className="bg-gray-50 dark:bg-gray-800">
                  <tr>
                    <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Line</th>
                    <th className="px-4 py-2 text-center text-xs font-medium text-gray-500 uppercase">Status</th>
                    <th className="px-4 py-2 text-center text-xs font-medium text-gray-500 uppercase">Availability</th>
                    <th className="px-4 py-2 text-center text-xs font-medium text-gray-500 uppercase">Performance</th>
                    <th className="px-4 py-2 text-center text-xs font-medium text-gray-500 uppercase">Quality</th>
                    <th className="px-4 py-2 text-center text-xs font-medium text-gray-500 uppercase">Overall OEE</th>
                  </tr>
                </thead>
                <tbody className="bg-white dark:bg-gray-900 divide-y divide-gray-200 dark:divide-gray-700">
                  {oeeData.lineBreakdown.map((line) => {
                    const lineStatus = getOEEStatus(line.overall)
                    const statusConfig = {
                      'running': { color: 'green', label: 'Running' },
                      'setup': { color: 'yellow', label: 'Setup' },
                      'down': { color: 'red', label: 'Down' },
                      'idle': { color: 'gray', label: 'Idle' }
                    }
                    const status = statusConfig[line.status] || statusConfig['idle']

                    return (
                      <tr key={line.lineId} className="hover:bg-gray-50 dark:hover:bg-gray-800">
                        <td className="px-4 py-2">
                          <div className="flex items-center">
                            <div className={`w-2 h-2 rounded-full mr-2 bg-${status.color}-500`}></div>
                            <span className="text-sm font-medium text-gray-900 dark:text-white">
                              {line.lineName}
                            </span>
                          </div>
                        </td>
                        <td className="px-4 py-2 text-center">
                          <span className={`inline-flex px-2 py-1 text-xs font-medium rounded-full bg-${status.color}-100 text-${status.color}-800`}>
                            {status.label}
                          </span>
                        </td>
                        <td className="px-4 py-2 text-center text-sm">{line.availability.toFixed(1)}%</td>
                        <td className="px-4 py-2 text-center text-sm">{line.performance.toFixed(1)}%</td>
                        <td className="px-4 py-2 text-center text-sm">{line.quality.toFixed(1)}%</td>
                        <td className="px-4 py-2 text-center">
                          <span className={`inline-flex px-2 py-1 text-sm font-medium rounded-full ${lineStatus.bgColor} ${lineStatus.textColor}`}>
                            {line.overall.toFixed(1)}%
                          </span>
                        </td>
                      </tr>
                    )
                  })}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {/* Performance Insights */}
        <div className="mt-6 p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
          <h4 className="text-sm font-medium text-gray-900 dark:text-white mb-2 flex items-center">
            <ExclamationTriangleIcon className="h-4 w-4 mr-1 text-blue-600" />
            Performance Insights
          </h4>
          <div className="space-y-2 text-sm text-gray-700 dark:text-gray-300">
            {oeeData.overall < oeeData.target && (
              <p>â€¢ Overall OEE is {(oeeData.target - oeeData.overall).toFixed(1)}% below target. Focus on improving the lowest performing component.</p>
            )}
            {oeeData.availability < 90 && (
              <p>â€¢ Availability could be improved by {(90 - oeeData.availability).toFixed(1)}% through better preventive maintenance.</p>
            )}
            {oeeData.performance < 95 && (
              <p>â€¢ Performance efficiency has potential for {(95 - oeeData.performance).toFixed(1)}% improvement through process optimization.</p>
            )}
            {oeeData.quality < 99 && (
              <p>â€¢ Quality rate can be enhanced by {(99 - oeeData.quality).toFixed(1)}% with improved process control.</p>
            )}
          </div>
        </div>
      </CardContent>
    </Card>
  )
})

export default OEEDisplay

import { useState } from 'react'

import { Card, CardContent, CardHeader, CardTitle , Badge , Alert, AlertDescription } from '../../../components/ui'

const defaultScenarios = [
  {
    id: 'base',
    name: 'Base Case',
    description: 'Current operational parameters',
    variables: {
      demandGrowth: 0,
      priceIncrease: 0,
      costReduction: 0,
      inventoryDays: 30,
      productionCapacity: 100
    },
    isActive: true,
    isBaseline: true
  },
  {
    id: 'optimistic',
    name: 'Optimistic Growth',
    description: '20% demand growth with 5% price increase',
    variables: {
      demandGrowth: 20,
      priceIncrease: 5,
      costReduction: 3,
      inventoryDays: 25,
      productionCapacity: 120
    },
    isActive: false,
    isBaseline: false
  },
  {
    id: 'conservative',
    name: 'Conservative',
    description: 'Slow growth with cost focus',
    variables: {
      demandGrowth: 5,
      priceIncrease: 2,
      costReduction: 10,
      inventoryDays: 35,
      productionCapacity: 95
    },
    isActive: false,
    isBaseline: false
  }
]

export function ScenarioBuilder({ onScenarioChange }) {
  const [scenarios, setScenarios] = useState(defaultScenarios)
  const [activeScenario, setActiveScenario] = useState('base')
  const [isEditing, setIsEditing] = useState(false)

  const handleVariableChange = (_scenarioId, _variable, value) => {
    setScenarios(prev =>
      prev.map(scenario =>
        scenario.id === scenarioId
          ? { ...scenario, variables: { ...scenario.variables, [variable]: Number(value) } }
          : scenario
      )
    )

    if (onScenarioChange) {
      const updatedScenario = scenarios.find(s => s.id === scenarioId)
      if (updatedScenario) {
        onScenarioChange({
          ...updatedScenario,
          variables: { ...updatedScenario.variables, [variable]: Number(value) }
        })
      }
    }
  }

  const handleScenarioSelect = (_scenarioId) => {
    setActiveScenario(scenarioId)
    setScenarios(prev =>
      prev.map(scenario => ({
        ...scenario,
        isActive: scenario.id === scenarioId
      }))
    )

    const selectedScenario = scenarios.find(s => s.id === scenarioId)
    if (selectedScenario && onScenarioChange) {
      onScenarioChange(selectedScenario)
    }
  }

  const currentScenario = scenarios.find(s => s.id === activeScenario)

  return (
    <div className="space-y-6">
      {/* Scenario Selection */}
      <Card>
        <CardHeader>
          <CardTitle>Select Scenario</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {scenarios.map(scenario => (
              <button
                key={scenario.id}
                onClick={() => handleScenarioSelect(scenario.id)}
                className={`p-4 rounded-lg border-2 transition-all text-left ${
                  scenario.isActive
                    ? 'border-blue-500 bg-blue-50 dark:bg-blue-950'
                    : 'border-gray-200 hover:border-gray-300'
                }`}
              >
                <div className="flex items-center justify-between mb-2">
                  <h3 className="font-semibold">{scenario.name}</h3>
                  {scenario.isBaseline && <Badge variant="info">Baseline</Badge>}
                </div>
                <p className="text-sm text-gray-600 dark:text-gray-400">
                  {scenario.description}
                </p>
              </button>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Variable Adjustments */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Scenario Variables: {currentScenario?.name}</CardTitle>
            <button
              onClick={() => setIsEditing(!isEditing)}
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors"
            >
              {isEditing ? 'Save Changes' : 'Edit Variables'}
            </button>
          </div>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {Object.entries(currentScenario?.variables || {}).map(([key, value]) => (
              <div key={key} className="space-y-2">
                <div className="flex justify-between items-center">
                  <label className="text-sm font-medium capitalize">
                    {key.replace(/([A-Z])/g, ' $1').trim()}
                  </label>
                  <span className="text-sm font-semibold">{value}%</span>
                </div>
                <input
                  type="range"
                  min="-50"
                  max="150"
                  value={value}
                  onChange={(e) => handleVariableChange(activeScenario, key, e.target.value)}
                  disabled={!isEditing || currentScenario?.isBaseline}
                  className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer dark:bg-gray-700"
                />
              </div>
            ))}
          </div>

          {currentScenario?.isBaseline && (
            <Alert variant="info" className="mt-4">
              <AlertDescription>
                The baseline scenario represents current operational parameters and cannot be edited.
              </AlertDescription>
            </Alert>
          )}
        </CardContent>
      </Card>

      {/* Impact Summary */}
      <Card>
        <CardHeader>
          <CardTitle>Projected Impact</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="space-y-1">
              <p className="text-sm text-gray-600">Revenue Impact</p>
              <p className="text-2xl font-bold text-green-600">
                +{((currentScenario?.variables.demandGrowth || 0) * 1.5).toFixed(1)}%
              </p>
            </div>
            <div className="space-y-1">
              <p className="text-sm text-gray-600">Cost Savings</p>
              <p className="text-2xl font-bold text-blue-600">
                ${((currentScenario?.variables.costReduction || 0) * 10000).toLocaleString()}
              </p>
            </div>
            <div className="space-y-1">
              <p className="text-sm text-gray-600">Capacity Utilization</p>
              <p className="text-2xl font-bold text-purple-600">
                {currentScenario?.variables.productionCapacity || 100}%
              </p>
            </div>
            <div className="space-y-1">
              <p className="text-sm text-gray-600">Working Capital Days</p>
              <p className="text-2xl font-bold text-orange-600">
                {currentScenario?.variables.inventoryDays || 30} days
              </p>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  )
}

import { useState, useEffect } from 'react'
