          min: '2020-01-01',
          maxDaysFromNow: 0
        },
        transaction_type: {
          required: true,
          type: 'string',
          enum: ['revenue', 'cost', 'expense', 'tax', 'fee', 'adjustment']
        },
        amount: {
          required: true,
          type: 'number',
          precision: 2
        },
        currency: {
          required: true,
          type: 'string',
          enum: ['GBP', 'EUR', 'USD']
        },
        account_code: {
          required: false,
          type: 'string',
          format: /^\d{4}-\d{2}$/,
          description: 'Format: XXXX-XX'
        }
      }
    };
  }

  /**
   * Validate a single row of data
   * @param {Object} data - Row data to validate
   * @param {string} dataType - Type of data (products, historical_sales, etc.)
   * @param {number} rowNumber - Row number for error reporting
   * @param {Object} context - Additional context (existing data, etc.)
   * @returns {Object} Validation result
   */
  async validateRow(data, dataType, rowNumber, context = {}) {
    const result = {
      isValid: true,
      errors: [],
      warnings: [],
      processedData: { ...data }
    };

    const rules = this.validationRules[dataType];
    if (!rules) {
      result.errors.push({
        field: 'dataType',
        message: `Unknown data type: ${dataType}`,
        code: 'UNKNOWN_DATA_TYPE'
      });
      result.isValid = false;
      return result;
    }

    // Validate each field
    for (const [fieldName, fieldRules] of Object.entries(rules)) {
      const fieldResult = await this.validateField(
        data[fieldName], 
        fieldName, 
        fieldRules, 
        data, 
        rowNumber, 
        context
      );

      if (!fieldResult.isValid) {
        result.isValid = false;
        result.errors.push(...fieldResult.errors);
      }

      result.warnings.push(...fieldResult.warnings);

      // Use processed value if different
      if (fieldResult.processedValue !== undefined) {
        result.processedData[fieldName] = fieldResult.processedValue;
      }
    }

    // Validate business rules
    const businessRuleResult = this.validateBusinessRules(result.processedData, dataType, rowNumber);
    if (!businessRuleResult.isValid) {
      result.isValid = false;
      result.errors.push(...businessRuleResult.errors);
    }
    result.warnings.push(...businessRuleResult.warnings);

    return result;
  }

  /**
   * Validate a single field
   */
  async validateField(value, fieldName, rules, rowData, rowNumber, context) {
    const result = {
      isValid: true,
      errors: [],
      warnings: [],
      processedValue: value
    };

    // Required field validation
    if (rules.required && (value === null || value === undefined || value === '')) {
      result.errors.push({
        field: fieldName,
        message: `Required field '${fieldName}' is missing`,
        code: 'REQUIRED_FIELD_MISSING',
        rowNumber
      });
      result.isValid = false;
      return result;
    }

    // Skip other validations if value is empty and not required
    if (!rules.required && (value === null || value === undefined || value === '')) {
      return result;
    }

    // Type validation and conversion
    const typeResult = this.validateType(value, rules.type, fieldName, rowNumber);
    if (!typeResult.isValid) {
      result.errors.push(...typeResult.errors);
      result.isValid = false;
    } else {
      result.processedValue = typeResult.processedValue;
      value = typeResult.processedValue;
    }

    // Format validation
    if (rules.format && typeof value === 'string') {
      if (!rules.format.test(value)) {
        result.errors.push({
          field: fieldName,
          message: `Invalid format for '${fieldName}'. ${rules.description || ''}`,
          code: 'INVALID_FORMAT',
          rowNumber
        });
        result.isValid = false;
      }
    }

    // Range validation
    if (typeof value === 'number') {
      if (rules.min !== undefined && value < rules.min) {
        result.errors.push({
          field: fieldName,
          message: `Value ${value} is below minimum ${rules.min} for '${fieldName}'`,
          code: 'VALUE_BELOW_MINIMUM',
          rowNumber
        });
        result.isValid = false;
      }

      if (rules.max !== undefined && value > rules.max) {
        result.errors.push({
          field: fieldName,
          message: `Value ${value} exceeds maximum ${rules.max} for '${fieldName}'`,
          code: 'VALUE_ABOVE_MAXIMUM',
          rowNumber
        });
        result.isValid = false;
      }
    }

    // String length validation
    if (typeof value === 'string') {
      if (rules.minLength && value.length < rules.minLength) {
        result.errors.push({
          field: fieldName,
          message: `Text too short for '${fieldName}' (minimum ${rules.minLength} characters)`,
          code: 'TEXT_TOO_SHORT',
          rowNumber
        });
        result.isValid = false;
      }

      if (rules.maxLength && value.length > rules.maxLength) {
        result.errors.push({
          field: fieldName,
          message: `Text too long for '${fieldName}' (maximum ${rules.maxLength} characters)`,
          code: 'TEXT_TOO_LONG',
          rowNumber
        });
        result.isValid = false;
      }
    }

    // Enum validation
    if (rules.enum && !rules.enum.includes(value)) {
      result.errors.push({
        field: fieldName,
        message: `Invalid value '${value}' for '${fieldName}'. Allowed values: ${rules.enum.join(', ')}`,
        code: 'INVALID_ENUM_VALUE',
        rowNumber
      });
      result.isValid = false;
    }

    // Precision validation for numbers
    if (rules.precision && typeof value === 'number') {
      const decimalPlaces = (value.toString().split('.')[1] || '').length;
      if (decimalPlaces > rules.precision) {
        result.warnings.push({
          field: fieldName,
          message: `Value ${value} has more than ${rules.precision} decimal places`,
          code: 'PRECISION_WARNING',
          rowNumber
        });
        // Round to specified precision
        result.processedValue = parseFloat(value.toFixed(rules.precision));
      }
    }

    // Warning thresholds
    if (rules.warning && typeof value === 'number') {
      if (value < rules.warning.threshold) {
        result.warnings.push({
          field: fieldName,
          message: rules.warning.message || `Value ${value} is below warning threshold`,
          code: 'WARNING_THRESHOLD',
          rowNumber
        });
      }
    }

    return result;
  }

  /**
   * Validate and convert data types
   */
  validateType(value, expectedType, fieldName, rowNumber) {
    const result = {
      isValid: true,
      errors: [],
      processedValue: value
    };

    try {
      switch (expectedType) {
        case 'string':
          result.processedValue = String(value);
          break;

        case 'number':
          if (typeof value === 'string') {
            // Remove common currency symbols and whitespace
            const cleanValue = value.replace(/[$Â£â‚¬,\s]/g, '');
            const parsed = parseFloat(cleanValue);
            if (isNaN(parsed)) {
              throw new Error(`Cannot convert '${value}' to number`);
            }
            result.processedValue = parsed;
          } else if (typeof value !== 'number') {
            throw new Error(`Expected number, got ${typeof value}`);
          }
          break;

        case 'integer':
          if (typeof value === 'string') {
            const parsed = parseInt(value.replace(/[,\s]/g, ''), 10);
            if (isNaN(parsed)) {
              throw new Error(`Cannot convert '${value}' to integer`);
            }
            result.processedValue = parsed;
          } else if (typeof value === 'number') {
            result.processedValue = Math.floor(value);
          } else {
            throw new Error(`Expected integer, got ${typeof value}`);
          }
          break;

        case 'date':
          if (typeof value === 'string') {
            const date = new Date(value);
            if (isNaN(date.getTime())) {
              throw new Error(`Invalid date format: ${value}`);
            }
            result.processedValue = date.toISOString().split('T')[0]; // YYYY-MM-DD
          } else if (value instanceof Date) {
            result.processedValue = value.toISOString().split('T')[0];
          } else {
            throw new Error(`Expected date, got ${typeof value}`);
          }
          break;

        case 'boolean':
          if (typeof value === 'string') {
            const lowerValue = value.toLowerCase();
            if (['true', '1', 'yes', 'y'].includes(lowerValue)) {
              result.processedValue = true;
            } else if (['false', '0', 'no', 'n'].includes(lowerValue)) {
              result.processedValue = false;
            } else {
              throw new Error(`Cannot convert '${value}' to boolean`);
            }
          } else if (typeof value !== 'boolean') {
            throw new Error(`Expected boolean, got ${typeof value}`);
          }
          break;

        default:
          // No conversion needed
          break;
      }
    } catch (error) {
      result.errors.push({
        field: fieldName,
        message: `Type validation failed: ${error.message}`,
        code: 'TYPE_VALIDATION_ERROR',
        rowNumber
      });
      result.isValid = false;
    }

    return result;
  }

  /**
   * Validate business rules across fields
   */
  validateBusinessRules(data, dataType, rowNumber) {
    const result = {
      isValid: true,
      errors: [],
      warnings: []
    };

    const rules = this.validationRules[dataType];
    if (!rules) return result;

    // Check business rules defined in field definitions
    for (const [fieldName, fieldRules] of Object.entries(rules)) {
      if (fieldRules.businessRule) {
        const ruleResult = this.evaluateBusinessRule(
          fieldRules.businessRule, 
          data, 
          fieldName, 
          rowNumber
        );
        if (!ruleResult.isValid) {
          result.errors.push(...ruleResult.errors);
          result.isValid = false;
        }
        result.warnings.push(...ruleResult.warnings);
      }
    }

    // Data type specific business rules
    switch (dataType) {
      case 'products':
        if (data.selling_price && data.unit_cost && data.selling_price <= data.unit_cost) {
          result.errors.push({
            field: 'selling_price',
            message: 'Selling price must be greater than unit cost',
            code: 'BUSINESS_RULE_VIOLATION',
            rowNumber
          });
          result.isValid = false;
        }
        break;

      case 'historical_sales':
        // Calculate gross revenue if not provided
        if (data.quantity_sold && data.unit_price && !data.gross_revenue) {
          data.grossrevenue = data.quantity_sold * data.unit_price;
        }

        // Validate revenue calculations
        if (data.gross_revenue && data.quantity_sold && data.unit_price) {
          const expectedGross = data.quantity_sold * data.unit_price;
          const tolerance = 0.01;
          if (Math.abs(data.gross_revenue - expectedGross) > tolerance) {
            result.warnings.push({
              field: 'gross_revenue',
              message: `Gross revenue ${data.gross_revenue} doesn't match quantity Ã— price ${expectedGross}`,
              code: 'CALCULATION_MISMATCH',
              rowNumber
            });
          }
        }
        break;

      case 'inventory_levels':
        if (data.available_quantity && data.quantity_on_hand && data.reserved_quantity) {
          const expectedAvailable = data.quantity_on_hand - data.reserved_quantity;
          if (data.available_quantity !== expectedAvailable) {
            result.warnings.push({
              field: 'available_quantity',
              message: `Available quantity should be ${expectedAvailable} (on_hand - reserved)`,
              code: 'CALCULATION_MISMATCH',
              rowNumber
            });
          }
        }
        break;
    }

    return result;
  }

  /**
   * Evaluate a business rule string
   */
  evaluateBusinessRule(rule, data, fieldName, rowNumber) {
    const result = {
      isValid: true,
      errors: [],
      warnings: []
    };

    try {
      // Simple rule evaluation - in production, use a proper expression parser
      if (rule.includes('>')) {
        const [left, right] = rule.split('>').map(s => s.trim());
        const leftValue = this.getFieldValue(left, data);
        const rightValue = this.getFieldValue(right, data);

        if (leftValue !== null && rightValue !== null && leftValue <= rightValue) {
          result.errors.push({
            field: fieldName,
            message: `Business rule violation: ${rule}`,
            code: 'BUSINESS_RULE_VIOLATION',
            rowNumber
          });
          result.isValid = false;
        }
      } else if (rule.includes('>=')) {
        const [left, right] = rule.split('>=').map(s => s.trim());
        const leftValue = this.getFieldValue(left, data);
        const rightValue = this.getFieldValue(right, data);

        if (leftValue !== null && rightValue !== null && leftValue < rightValue) {
          result.errors.push({
            field: fieldName,
            message: `Business rule violation: ${rule}`,
            code: 'BUSINESS_RULE_VIOLATION',
            rowNumber
          });
          result.isValid = false;
        }
      } else if (rule.includes('=')) {
        // Calculation rules - mark as warning if values don't match
        const [left, right] = rule.split('=').map(s => s.trim());
        const leftValue = this.getFieldValue(left, data);
        const rightValue = this.evaluateExpression(right, data);

        if (leftValue !== null && rightValue !== null && Math.abs(leftValue - rightValue) > 0.01) {
          result.warnings.push({
            field: fieldName,
            message: `Calculated value mismatch: ${rule}`,
            code: 'CALCULATION_MISMATCH',
            rowNumber
          });
        }
      }
    } catch (error) {
      logWarn('Business rule evaluation failed', { rule, error: error.message });
    }

    return result;
  }

  /**
   * Get field value from data object
   */
  getFieldValue(fieldName, data) {
    return data[fieldName] !== undefined ? data[fieldName] : null;
  }

  /**
   * Evaluate simple mathematical expressions safely
   */
  evaluateExpression(expression, data) {
    try {
      // Simple expression evaluator - replace field names with values
      let expr = expression;
      for (const [field, value] of Object.entries(data)) {
        if (typeof value === 'number') {
          expr = expr.replace(new RegExp(field, 'g'), value.toString());
        }
      }

      // Basic math operations only - use safe evaluation
      if (/^[\d+\-*/.\s()]+$/.test(expr)) {
        return this.safeEvaluate(expr);
      }
    } catch (error) {
      logWarn('Expression evaluation failed', { expression, error: error.message });
    }
    return null;
  }

  /**
   * Safe mathematical expression evaluator without eval() or Function constructor
   */
  safeEvaluate(expression) {
    // Remove whitespace
    const expr = expression.replace(/\s/g, '');

    // Simple calculator for basic operations using manual parsing
    try {
      // Only allow mathematical operations
      if (!/^[\d+\-*/.()]+$/.test(expr)) {
        throw new Error('Invalid characters in expression');
      }

      // Use a simple recursive descent parser for safety
      const result = this.parseExpression(expr);

      if (typeof result !== 'number' || !isFinite(result)) {
        throw new Error('Invalid calculation result');
      }

      return result;
    } catch (error) {
      logWarn('Safe evaluation failed', { expression, error: error.message });
      return null;
    }
  }

  /**
   * Simple recursive descent parser for mathematical expressions
   */
  parseExpression(expr) {
    let index = 0;

    const parseNumber = () => {
      let num = '';
      while (index < expr.length && /[\d.]/.test(expr[index])) {
        num += expr[index++];
      }
      return parseFloat(num);
    };

    const parseFactor = () => {
      if (expr[index] === '(') {
        index++; // skip '('
        const result = parseExpression();
        index++; // skip ')'
        return result;
      }
      return parseNumber();
    };

    const parseTerm = () => {
      let result = parseFactor();
      while (index < expr.length && /[*/]/.test(expr[index])) {
        const op = expr[index++];
        const right = parseFactor();
        result = op === '*' ? result * right : result / right;
      }
      return result;
    };

    const parseExpression = () => {
      let result = parseTerm();
      while (index < expr.length && /[+\-]/.test(expr[index])) {
        const op = expr[index++];
        const right = parseTerm();
        result = op === '+' ? result + right : result - right;
      }
      return result;
    };

    return parseExpression();
  }

  /**
   * Validate an entire dataset
   * @param {Array} rows - Array of data rows
   * @param {string} dataType - Type of data
   * @param {Object} context - Additional context
   * @returns {Object} Validation summary
   */
  async validateDataset(rows, dataType, context = {}) {
    logInfo('Starting dataset validation', { 
      dataType, 
      rowCount: rows.length 
    });

    const results = {
      isValid: true,
      totalRows: rows.length,
      validRows: 0,
      errorRows: 0,
      warningRows: 0,
      results: [],
      summary: {
        errors: [],
        warnings: [],
        fieldStats: {}
      }
    };

    for (let i = 0; i < rows.length; i++) {
      const rowResult = await this.validateRow(rows[i], dataType, i + 1, context);
      results.results.push({
        rowNumber: i + 1,
        ...rowResult
      });

      if (rowResult.isValid) {
        results.validRows++;
      } else {
        results.errorRows++;
        results.isValid = false;
      }

      if (rowResult.warnings.length > 0) {
        results.warningRows++;
      }

      // Collect summary statistics
      rowResult.errors.forEach(error => {
        results.summary.errors.push(error);
      });

      rowResult.warnings.forEach(warning => {
        results.summary.warnings.push(warning);
      });
    }

    // Calculate completion percentage
    results.completionPercentage = results.totalRows > 0 
      ? Math.round((results.validRows / results.totalRows) * 100) 
      : 0;

    logInfo('Dataset validation completed', {
      dataType,
      totalRows: results.totalRows,
      validRows: results.validRows,
      errorRows: results.errorRows,
      completionPercentage: results.completionPercentage
    });

    return results;
  }
}

export default ValidationEngine;

import { logDebug, logInfo, logWarn, logError } from 'logger';
/**
 * Environment Variable Validator
 * Validates required environment variables on application startup
 * Implements TASK-002 from SpecKit specifications
 */

// Required environment variables for backend
const requiredBackendEnvVars = [
  'CLERK_SECRET_KEY',
  'DATABASE_URL_DEVELOPMENT',
  'DATABASE_URL_TESTING', 
  'DATABASE_URL_PRODUCTION'
];

// Required environment variables for frontend
const requiredFrontendEnvVars = [
  'VITE_CLERK_PUBLISHABLE_KEY',
  'VITE_CLERK_DOMAIN'
];

/**
 * Validates backend environment variables
 * Called from server startup
 */
export function validateBackendEnvironment() {
  logDebug('ðŸ” Validating backend environment variables...');

  const missing = requiredBackendEnvVars.filter(
    key => !process.env[key] || process.env[key].trim() === ''
  );

  if (missing.length > 0) {
    logError('âŒ CRITICAL: Missing required backend environment variables:');
    missing.forEach(key => {
      logError(`   - ${key}`);
    });
    logError('\nðŸ“‹ Required environment variables:');
    requiredBackendEnvVars.forEach(key => {
      const status = process.env[key] ? 'âœ…' : 'âŒ';
      const value = process.env[key] ? '[SET]' : '[MISSING]';
      logError(`   ${status} ${key}: ${value}`);
    });
    logError('\nðŸ”§ Please set these environment variables and restart the application.');
    process.exit(1);
  }

  // Validate Clerk secret key format
  if (process.env.CLERK_SECRET_KEY && !process.env.CLERK_SECRET_KEY.startsWith('sk_')) {
    logError('âŒ CRITICAL: CLERK_SECRET_KEY must start with "sk_"');
    process.exit(1);
  }

  // Validate database URLs format
  const dbUrls = [
    'DATABASE_URL_DEVELOPMENT',
    'DATABASE_URL_TESTING', 
    'DATABASE_URL_PRODUCTION'
  ];

  dbUrls.forEach(key => {
    if (process.env[key] && !process.env[key].startsWith('postgresql://')) {
      logError(`âŒ CRITICAL: ${key} must be a valid PostgreSQL connection string`);
      process.exit(1);
    }
  });

  logDebug('âœ… Backend environment validation passed');

  // Log configuration summary (without sensitive values)
  logDebug('ðŸ“‹ Environment Configuration:');
  logDebug(`   - CLERK_SECRET_KEY: ${process.env.CLERK_SECRET_KEY ? 'SET' : 'MISSING'}`);
  logDebug(`   - DATABASE_URL_DEVELOPMENT: ${process.env.DATABASE_URL_DEVELOPMENT ? 'SET' : 'MISSING'}`);
  logDebug(`   - DATABASE_URL_TESTING: ${process.env.DATABASE_URL_TESTING ? 'SET' : 'MISSING'}`);
  logDebug(`   - DATABASE_URL_PRODUCTION: ${process.env.DATABASE_URL_PRODUCTION ? 'SET' : 'MISSING'}`);
}

/**
 * Validates frontend environment variables
 * Called from React app startup
 */
export function validateFrontendEnvironment() {
  logDebug('ðŸ” Validating frontend environment variables...');

  const missing = requiredFrontendEnvVars.filter(
    key => !import.meta.env[key] || import.meta.env[key].trim() === ''
  );

  if (missing.length > 0) {
    logError('âŒ CRITICAL: Missing required frontend environment variables:');
    missing.forEach(key => {
      logError(`   - ${key}`);
    });
    logError('\nðŸ“‹ Required environment variables:');
    requiredFrontendEnvVars.forEach(key => {
      const status = import.meta.env[key] ? 'âœ…' : 'âŒ';
      const value = import.meta.env[key] ? '[SET]' : '[MISSING]';
      logError(`   ${status} ${key}: ${value}`);
    });
    logError('\nðŸ”§ Please set these environment variables in .env.local and restart the application.');

    // For frontend, show user-friendly error instead of crashing
    return false;
  }

  // Validate Clerk publishable key format
  if (import.meta.env.VITE_CLERK_PUBLISHABLE_KEY && !import.meta.env.VITE_CLERK_PUBLISHABLE_KEY.startsWith('pk_')) {
    logError('âŒ CRITICAL: VITE_CLERK_PUBLISHABLE_KEY must start with "pk_"');
    return false;
  }

  logDebug('âœ… Frontend environment validation passed');

  // Log configuration summary (without sensitive values)
  logDebug('ðŸ“‹ Frontend Environment Configuration:');
  logDebug(`   - VITE_CLERK_PUBLISHABLE_KEY: ${import.meta.env.VITE_CLERK_PUBLISHABLE_KEY ? 'SET' : 'MISSING'}`);
  logDebug(`   - VITE_CLERK_DOMAIN: ${import.meta.env.VITE_CLERK_DOMAIN ? 'SET' : 'MISSING'}`);

  return true;
}

/**
 * Get environment validation status for health checks
 */
export function getEnvironmentStatus() {
  const backendStatus = requiredBackendEnvVars.every(
    key => process.env[key] && process.env[key].trim() !== ''
  );

  return {
    backend: backendStatus,
    timestamp: new Date().toISOString()
  };
}

/**
 * Validate specific environment variable
 */
export function validateEnvVar(key, value, pattern = null) {
  if (!value || value.trim() === '') {
    return { valid: false, error: `${key} is required but not set` };
  }

  if (pattern && !pattern.test(value)) {
    return { valid: false, error: `${key} does not match required pattern` };
  }

  return { valid: true };
}
