/**
 * Dashboard API Endpoints
 *
 * Provides aggregated data for the executive dashboard:
 * - KPI summary data (6 key metrics)
 * - Time series data for charts (sales, production, inventory)
 * - Working capital snapshot
 * - Recent alerts and notifications
 *
 * Target: <3 second response time for full dashboard load
 */

const express = require('express');
const router = express.Router();

/**
 * GET /api/v1/dashboard/executive
 *
 * Returns complete dashboard data including:
 * - KPIs (revenue, production, inventory, CCC, OTD, forecast accuracy)
 * - Chart data (sales/revenue, production output, inventory levels)
 * - Working capital summary
 * - Recent alerts
 *
 * Response time target: <500ms
 */
router.get('/executive', async (req, res) => {
  const startTime = Date.now();

  try {
    // TODO: Replace with real data fetching from services
    // For now, return structured mock data that components expect

    const dashboardData = {
      kpis: generateKPIData(),
      charts: {
        salesRevenue: generateSalesRevenueData(),
        productionOutput: generateProductionOutputData(),
        inventoryLevels: generateInventoryLevelsData(),
      },
      workingCapital: generateWorkingCapitalData(),
      recentAlerts: generateRecentAlerts(),
      metadata: {
        timestamp: new Date().toISOString(),
        responseTime: null, // Will be set below
      },
    };

    const responseTime = Date.now() - startTime;
    dashboardData.metadata.responseTime = responseTime;

    // Log performance warning if response time exceeds target
    if (responseTime > 500) {
      console.warn(`[Dashboard API] Response time: ${responseTime}ms (target: <500ms)`);
    }

    res.json({
      success: true,
      data: dashboardData,
    });
  } catch (error) {
    console.error('[Dashboard API] Error fetching dashboard data:', error);
    res.status(500).json({
      success: false,
      error: 'Failed to fetch dashboard data',
      message: error.message,
    });
  }
});

/**
 * GET /api/v1/dashboard/kpis
 *
 * Returns only KPI data (for quick updates)
 */
router.get('/kpis', async (req, res) => {
  try {
    const kpiData = generateKPIData();

    res.json({
      success: true,
      data: kpiData,
    });
  } catch (error) {
    console.error('[Dashboard API] Error fetching KPI data:', error);
    res.status(500).json({
      success: false,
      error: 'Failed to fetch KPI data',
      message: error.message,
    });
  }
});

/**
 * Helper Functions to Generate Dashboard Data
 *
 * TODO: Replace these with real data fetching from:
 * - Shopify API (sales/revenue)
 * - Production database (manufacturing data)
 * - Inventory management system
 * - Financial services (working capital)
 * - Forecasting engine (accuracy metrics)
 */

function generateKPIData() {
  const now = new Date();

  return {
    revenue: {
      today: 95000,
      mtd: 1850000,
      ytd: 18500000,
      target: 100000,
      change: 12.5,
      sparkline: generateSparkline(30, 80000, 110000),
    },
    production: {
      units: 12500,
      target: 13000,
      oee: 87.3,
      sparkline: generateSparkline(30, 11000, 14000),
    },
    inventory: {
      value: 425000,
      units: 8450,
      skus: 9,
      change: -3.2,
      sparkline: generateSparkline(30, 400000, 450000),
    },
    ccc: {
      days: 52,
      dio: 35,
      dso: 28,
      dpo: 11,
      sparkline: generateSparkline(30, 48, 58),
    },
    otd: {
      rate: 94.5,
      onTime: 189,
      total: 200,
      change: 2.1,
      sparkline: generateSparkline(30, 92, 96),
    },
    forecast: {
      accuracy: 88.2,
      mape: 11.8,
      models: 4,
      sparkline: generateSparkline(30, 85, 92),
    },
  };
}

function generateSalesRevenueData() {
  const data = [];
  const now = new Date();

  // Generate 90 days of data
  for (let i = 90; i >= 0; i--) {
    const date = new Date(now);
    date.setDate(date.getDate() - i);

    data.push({
      date: date.toISOString().split('T')[0],
      revenue: Math.floor(80000 + Math.random() * 40000 + Math.sin(i / 7) * 15000),
      orders: Math.floor(150 + Math.random() * 100 + Math.sin(i / 7) * 30),
    });
  }

  return data;
}

function generateProductionOutputData() {
  const data = [];
  const now = new Date();

  // Generate 90 days of data
  for (let i = 90; i >= 0; i--) {
    const date = new Date(now);
    date.setDate(date.getDate() - i);

    // Skip weekends
    if (date.getDay() === 0 || date.getDay() === 6) {
      continue;
    }

    const baseOutput = 12000;
    const variation = Math.random() * 3000;
    const unitsProduced = Math.floor(baseOutput + variation);
    const target = 13000;

    data.push({
      date: date.toISOString().split('T')[0],
      unitsProduced,
      target,
      targetPercent: Math.round((unitsProduced / target) * 100),
      oee: Math.round(75 + Math.random() * 20),
      oeeTarget: 85,
    });
  }

  return data;
}

function generateInventoryLevelsData() {
  const timeSeriesData = [];
  const now = new Date();

  // Generate 90 days of inventory time series
  for (let i = 90; i >= 0; i--) {
    const date = new Date(now);
    date.setDate(date.getDate() - i);

    timeSeriesData.push({
      date: date.toISOString().split('T')[0],
      inventoryValue: Math.floor(400000 + Math.random() * 60000 + Math.sin(i / 14) * 40000),
      totalUnits: Math.floor(8000 + Math.random() * 1200 + Math.sin(i / 14) * 800),
      reorderPoint: 600,
      turnoverRate: Math.round(30 + Math.random() * 10),
    });
  }

  // Generate SKU-level data
  const skuData = [
    { sku: 'PROD-001', currentStock: 1200, reorderPoint: 800 },
    { sku: 'PROD-002', currentStock: 980, reorderPoint: 900 },
    { sku: 'PROD-003', currentStock: 750, reorderPoint: 700 },
    { sku: 'PROD-004', currentStock: 650, reorderPoint: 800 },
    { sku: 'PROD-005', currentStock: 1100, reorderPoint: 750 },
    { sku: 'PROD-006', currentStock: 580, reorderPoint: 600 },
    { sku: 'PROD-007', currentStock: 420, reorderPoint: 650 },
    { sku: 'PROD-008', currentStock: 890, reorderPoint: 550 },
    { sku: 'PROD-009', currentStock: 680, reorderPoint: 500 },
  ];

  return {
    timeSeries: timeSeriesData,
    skuData,
  };
}

function generateWorkingCapitalData() {
  return {
    ccc: {
      value: 52,
      status: 'good',
      components: {
        dio: 35,
        dso: 28,
        dpo: 11,
      },
    },
    runway: {
      months: 8.5,
      cashBalance: 425000,
      burnRate: 50000,
      projection: generateRunwayProjection(),
    },
    breaches: [
      {
        month: 7,
        date: new Date(Date.now() + 7 * 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
        projectedBalance: -15000,
        deficit: 15000,
        severity: 'warning',
      },
    ],
    mitigationActions: [
      {
        id: 'accelerate-collections',
        title: 'Accelerate Collections',
        description: 'Reduce DSO by 5 days',
        impact: 15000,
      },
      {
        id: 'extend-payables',
        title: 'Extend Payables',
        description: 'Negotiate 15-day extension',
        impact: 12000,
      },
      {
        id: 'optimize-inventory',
        title: 'Optimize Inventory',
        description: 'Reduce DIO by 3 days',
        impact: 8500,
      },
    ],
  };
}

function generateRunwayProjection() {
  const projection = [];
  let balance = 425000;
  const burnRate = 50000;

  for (let i = 0; i < 12; i++) {
    balance -= burnRate;
    projection.push({
      month: i,
      balance,
    });
  }

  return projection;
}

function generateRecentAlerts() {
  return [
    {
      id: 'alert-1',
      type: 'warning',
      title: 'Low Inventory: PROD-007',
      message: 'Stock level below reorder point',
      timestamp: new Date(Date.now() - 2 * 60 * 60 * 1000).toISOString(),
    },
    {
      id: 'alert-2',
      type: 'info',
      title: 'Production Target Achieved',
      message: 'Daily production goal exceeded by 8%',
      timestamp: new Date(Date.now() - 5 * 60 * 60 * 1000).toISOString(),
    },
    {
      id: 'alert-3',
      type: 'critical',
      title: 'Cash Runway Warning',
      message: 'Projected cash deficit in 7 months',
      timestamp: new Date(Date.now() - 12 * 60 * 60 * 1000).toISOString(),
    },
  ];
}

function generateSparkline(points, min, max) {
  const data = [];
  for (let i = 0; i < points; i++) {
    data.push(Math.floor(min + Math.random() * (max - min)));
  }
  return data;
}

module.exports = router;

const { Queue, QueueEvents } = require('bullmq');
const { createBullMQConnection } = require('../lib/redis');
const logger = require('../utils/logger');

/**
 * QueueManager
 *
 * Central management for all BullMQ queues.
 *
 * Features:
 * - Queue initialization and lifecycle
 * - Event handling and logging
 * - Dead Letter Queue (DLQ) support
 * - Queue monitoring
 * - Job scheduling
 *
 * Queues:
 * - forecast-queue: AI forecast computations
 * - optimization-queue: Inventory optimization jobs
 * - sync-queue: API integration syncs
 * - import-queue: CSV/Excel data imports
 * - export-queue: Report generation
 * - notification-queue: Email/alert notifications
 * - analytics-queue: Background analytics calculations
 */
class QueueManager {
  constructor() {
    this.queues = new Map();
    this.queueEvents = new Map();
    this.connection = null;

    // Queue configurations
    this.queueConfigs = {
      forecast: {
        name: 'forecast-queue',
        defaultJobOptions: {
          attempts: 3,
          backoff: {
            type: 'exponential',
            delay: 2000, // Start with 2 seconds
          },
          removeOnComplete: {
            age: 24 * 3600, // Keep completed jobs for 24 hours
            count: 1000, // Keep last 1000 completed jobs
          },
          removeOnFail: {
            age: 7 * 24 * 3600, // Keep failed jobs for 7 days
          },
          timeout: 5 * 60 * 1000, // 5 minutes max
        },
      },
      optimization: {
        name: 'optimization-queue',
        defaultJobOptions: {
          attempts: 3,
          backoff: {
            type: 'exponential',
            delay: 2000,
          },
          removeOnComplete: {
            age: 24 * 3600,
            count: 1000,
          },
          removeOnFail: {
            age: 7 * 24 * 3600,
          },
          timeout: 5 * 60 * 1000,
        },
      },
      sync: {
        name: 'sync-queue',
        defaultJobOptions: {
          attempts: 5, // More retries for syncs
          backoff: {
            type: 'exponential',
            delay: 5000, // Start with 5 seconds
          },
          removeOnComplete: {
            age: 24 * 3600,
            count: 500,
          },
          removeOnFail: {
            age: 7 * 24 * 3600,
          },
          timeout: 10 * 60 * 1000, // 10 minutes for syncs
        },
      },
      import: {
        name: 'import-queue',
        defaultJobOptions: {
          attempts: 2, // Fewer retries for imports
          backoff: {
            type: 'fixed',
            delay: 5000,
          },
          removeOnComplete: {
            age: 7 * 24 * 3600, // Keep import jobs longer
            count: 100,
          },
          removeOnFail: {
            age: 30 * 24 * 3600, // Keep failed imports for 30 days
          },
          timeout: 15 * 60 * 1000, // 15 minutes for large imports
        },
      },
      export: {
        name: 'export-queue',
        defaultJobOptions: {
          attempts: 2,
          backoff: {
            type: 'fixed',
            delay: 3000,
          },
          removeOnComplete: {
            age: 3 * 24 * 3600, // Keep exports for 3 days
            count: 100,
          },
          removeOnFail: {
            age: 7 * 24 * 3600,
          },
          timeout: 10 * 60 * 1000,
        },
      },
      notification: {
        name: 'notification-queue',
        defaultJobOptions: {
          attempts: 5, // Important to deliver notifications
          backoff: {
            type: 'exponential',
            delay: 1000,
          },
          removeOnComplete: {
            age: 24 * 3600,
            count: 10000, // Keep more notifications
          },
          removeOnFail: {
            age: 7 * 24 * 3600,
          },
          timeout: 30 * 1000, // 30 seconds max
        },
      },
      analytics: {
        name: 'analytics-queue',
        defaultJobOptions: {
          attempts: 3,
          backoff: {
            type: 'exponential',
            delay: 2000,
          },
          removeOnComplete: {
            age: 24 * 3600,
            count: 500,
          },
          removeOnFail: {
            age: 7 * 24 * 3600,
          },
          timeout: 10 * 60 * 1000,
        },
      },
    };
  }

  /**
   * Initialize all queues
   */
  async initialize() {
    try {
      logger.info('[QueueManager] Initializing queues...');

      // Create Redis connection for BullMQ
      this.connection = createBullMQConnection();

      // Test connection
      await this.connection.ping();
      logger.info('[QueueManager] Redis connection established');

      // Initialize each queue
      for (const [key, config] of Object.entries(this.queueConfigs)) {
        await this.initializeQueue(key, config);
      }

      logger.info(`[QueueManager] Initialized ${this.queues.size} queues`);

      return {
        success: true,
        queues: Array.from(this.queues.keys()),
      };
    } catch (error) {
      logger.error('[QueueManager] Failed to initialize queues:', error);
      throw error;
    }
  }

  /**
   * Initialize a single queue
   */
  async initializeQueue(key, config) {
    try {
      // Create queue
      const queue = new Queue(config.name, {
        connection: this.connection,
        defaultJobOptions: config.defaultJobOptions,
      });

      // Create queue events for monitoring
      const queueEvents = new QueueEvents(config.name, {
        connection: createBullMQConnection(), // Separate connection for events
      });

      // Store references
      this.queues.set(key, queue);
      this.queueEvents.set(key, queueEvents);

      // Setup event listeners
      this.setupEventListeners(key, queue, queueEvents);

      logger.info(`[QueueManager] Initialized queue: ${config.name}`);

      return queue;
    } catch (error) {
      logger.error(`[QueueManager] Failed to initialize queue ${key}:`, error);
      throw error;
    }
  }

  /**
   * Setup event listeners for queue
   */
  setupEventListeners(key, queue, queueEvents) {
    // Job added
    queueEvents.on('added', ({ jobId }) => {
      logger.debug(`[Queue:${key}] Job added: ${jobId}`);
    });

    // Job completed
    queueEvents.on('completed', ({ jobId, returnvalue }) => {
      logger.info(`[Queue:${key}] Job completed: ${jobId}`);
    });

    // Job failed
    queueEvents.on('failed', ({ jobId, failedReason }) => {
      logger.error(`[Queue:${key}] Job failed: ${jobId}`, {
        reason: failedReason,
      });
    });

    // Job progress
    queueEvents.on('progress', ({ jobId, data }) => {
      logger.debug(`[Queue:${key}] Job progress: ${jobId}`, data);
    });

    // Job stalled
    queueEvents.on('stalled', ({ jobId }) => {
      logger.warn(`[Queue:${key}] Job stalled: ${jobId}`);
    });

    // Job retrying
    queueEvents.on('retrying', ({ jobId, attemptsMade }) => {
      logger.warn(`[Queue:${key}] Job retrying: ${jobId} (attempt ${attemptsMade})`);
    });

    // Error event
    queueEvents.on('error', (err) => {
      logger.error(`[Queue:${key}] Queue error:`, err);
    });
  }

  /**
   * Get queue by key
   */
  getQueue(key) {
    const queue = this.queues.get(key);
    if (!queue) {
      throw new Error(`Queue not found: ${key}`);
    }
    return queue;
  }

  /**
   * Add job to queue
   */
  async addJob(queueKey, jobName, data, options = {}) {
    try {
      const queue = this.getQueue(queueKey);

      const job = await queue.add(jobName, data, {
        ...options,
        // Merge with default options if not specified
        priority: options.priority || undefined,
        delay: options.delay || undefined,
        jobId: options.jobId || undefined,
      });

      logger.info(`[QueueManager] Job added to ${queueKey}: ${job.id}`);

      return {
        success: true,
        jobId: job.id,
        queueName: queue.name,
      };
    } catch (error) {
      logger.error(`[QueueManager] Failed to add job to ${queueKey}:`, error);
      throw error;
    }
  }

  /**
   * Get job status
   */
  async getJobStatus(queueKey, jobId) {
    try {
      const queue = this.getQueue(queueKey);
      const job = await queue.getJob(jobId);

      if (!job) {
        return null;
      }

      const state = await job.getState();
      const progress = job.progress;

      return {
        id: job.id,
        name: job.name,
        data: job.data,
        state,
        progress,
        attemptsMade: job.attemptsMade,
        timestamp: job.timestamp,
        processedOn: job.processedOn,
        finishedOn: job.finishedOn,
        failedReason: job.failedReason,
        returnvalue: job.returnvalue,
      };
    } catch (error) {
      logger.error(`[QueueManager] Failed to get job status:`, error);
      return null;
    }
  }

  /**
   * Get queue statistics
   */
  async getQueueStats(queueKey) {
    try {
      const queue = this.getQueue(queueKey);

      const [
        waiting,
        active,
        completed,
        failed,
        delayed,
        isPaused,
      ] = await Promise.all([
        queue.getWaitingCount(),
        queue.getActiveCount(),
        queue.getCompletedCount(),
        queue.getFailedCount(),
        queue.getDelayedCount(),
        queue.isPaused(),
      ]);

      return {
        queueName: queue.name,
        waiting,
        active,
        completed,
        failed,
        delayed,
        isPaused,
        total: waiting + active + completed + failed + delayed,
      };
    } catch (error) {
      logger.error(`[QueueManager] Failed to get queue stats:`, error);
      return null;
    }
  }

  /**
   * Get all queue statistics
   */
  async getAllQueueStats() {
    const stats = [];

    for (const key of this.queues.keys()) {
      const stat = await this.getQueueStats(key);
      if (stat) {
        stats.push({
          key,
          ...stat,
        });
      }
    }

    return stats;
  }

  /**
   * Get failed jobs
   */
  async getFailedJobs(queueKey, start = 0, end = 10) {
    try {
      const queue = this.getQueue(queueKey);
      const jobs = await queue.getFailed(start, end);

      return jobs.map(job => ({
        id: job.id,
        name: job.name,
        data: job.data,
        failedReason: job.failedReason,
        attemptsMade: job.attemptsMade,
        timestamp: job.timestamp,
        finishedOn: job.finishedOn,
      }));
    } catch (error) {
      logger.error(`[QueueManager] Failed to get failed jobs:`, error);
      return [];
    }
  }

  /**
   * Retry failed job
   */
  async retryJob(queueKey, jobId) {
    try {
      const queue = this.getQueue(queueKey);
      const job = await queue.getJob(jobId);

      if (!job) {
        throw new Error(`Job not found: ${jobId}`);
      }

      await job.retry();

      logger.info(`[QueueManager] Job retried: ${jobId}`);

      return {
        success: true,
        jobId,
      };
    } catch (error) {
      logger.error(`[QueueManager] Failed to retry job:`, error);
      throw error;
    }
  }

  /**
   * Remove job
   */
  async removeJob(queueKey, jobId) {
    try {
      const queue = this.getQueue(queueKey);
      const job = await queue.getJob(jobId);

      if (!job) {
        throw new Error(`Job not found: ${jobId}`);
      }

      await job.remove();

      logger.info(`[QueueManager] Job removed: ${jobId}`);

      return {
        success: true,
        jobId,
      };
    } catch (error) {
      logger.error(`[QueueManager] Failed to remove job:`, error);
      throw error;
    }
  }

  /**
   * Pause queue
   */
  async pauseQueue(queueKey) {
    try {
      const queue = this.getQueue(queueKey);
      await queue.pause();

      logger.info(`[QueueManager] Queue paused: ${queueKey}`);

      return { success: true };
    } catch (error) {
      logger.error(`[QueueManager] Failed to pause queue:`, error);
      throw error;
    }
  }

  /**
   * Resume queue
   */
  async resumeQueue(queueKey) {
    try {
      const queue = this.getQueue(queueKey);
      await queue.resume();

      logger.info(`[QueueManager] Queue resumed: ${queueKey}`);

      return { success: true };
    } catch (error) {
      logger.error(`[QueueManager] Failed to resume queue:`, error);
      throw error;
    }
  }

  /**
   * Clean queue
   */
  async cleanQueue(queueKey, grace = 3600000, limit = 1000, type = 'completed') {
    try {
      const queue = this.getQueue(queueKey);
      const jobs = await queue.clean(grace, limit, type);

      logger.info(`[QueueManager] Cleaned ${jobs.length} ${type} jobs from ${queueKey}`);

      return {
        success: true,
        cleaned: jobs.length,
      };
    } catch (error) {
      logger.error(`[QueueManager] Failed to clean queue:`, error);
      throw error;
    }
  }

  /**
   * Drain queue (remove all jobs)
   */
  async drainQueue(queueKey) {
    try {
      const queue = this.getQueue(queueKey);
      await queue.drain();

      logger.warn(`[QueueManager] Queue drained: ${queueKey}`);

      return { success: true };
    } catch (error) {
      logger.error(`[QueueManager] Failed to drain queue:`, error);
      throw error;
    }
  }

  /**
   * Obliterate queue (remove all data)
   */
  async obliterateQueue(queueKey) {
    try {
      const queue = this.getQueue(queueKey);
      await queue.obliterate({ force: true });

      logger.warn(`[QueueManager] Queue obliterated: ${queueKey}`);

      return { success: true };
    } catch (error) {
      logger.error(`[QueueManager] Failed to obliterate queue:`, error);
      throw error;
    }
  }

  /**
   * Shutdown all queues
   */
  async shutdown() {
    try {
      logger.info('[QueueManager] Shutting down queues...');

      // Close all queue events
      for (const [key, queueEvents] of this.queueEvents) {
        await queueEvents.close();
        logger.debug(`[QueueManager] Closed events for ${key}`);
      }

      // Close all queues
      for (const [key, queue] of this.queues) {
        await queue.close();
        logger.debug(`[QueueManager] Closed queue ${key}`);
      }

      // Close Redis connection
      if (this.connection) {
        await this.connection.quit();
        this.connection = null;
      }

      // Clear maps
      this.queues.clear();
      this.queueEvents.clear();

      logger.info('[QueueManager] Shutdown complete');

      return { success: true };
    } catch (error) {
      logger.error('[QueueManager] Shutdown error:', error);
      throw error;
    }
  }
}

// Singleton instance
let instance = null;

module.exports = {
  QueueManager,
  getInstance: () => {
    if (!instance) {
      instance = new QueueManager();
    }
    return instance;
  },
};

import express from 'express'

import { verifyUserCredentials, initializeDefaultUsers } from '../../lib/user-service.js'
import { logInfo, logError, logWarn } from '../../services/observability/structuredLogger.js'
import authMiddleware from '../middleware/authMiddleware.js'
import mfaMiddleware from '../middleware/mfaMiddleware.js'
import sessionMiddleware from '../middleware/sessionMiddleware.js'
import auditMiddleware from '../middleware/auditMiddleware.js'

const router = express.Router()

// Initialize default users on server startup
;(async () => {
  try {
    await initializeDefaultUsers()
    logInfo('Default users initialized')
  } catch (error) {
    logError('Failed to initialize default users', error)
  }
})()

// Sign in endpoint
router.post('/signin', async (req, res) => {
  try {
    const { email, password } = req.body

    if (!email || !password) {
      logWarn('Sign in attempt with missing credentials')
      return res.status(400).json({ error: 'Email and password are required' })
    }

    logInfo('Sign in attempt', { email })
    const user = await verifyUserCredentials(email, password)

    if (user) {
      // Create session token (in production, use JWT or proper session management)
