  ...SYSTEM_TOOL_SCHEMAS,
  ...MANUFACTURING_TOOL_SCHEMAS,
  ...FINANCIAL_TOOL_SCHEMAS,
  ...INTEGRATION_TOOL_SCHEMAS,
}

/**
 * Get schema for a specific tool
 */
export function getToolSchema(toolName, schemaType = 'input') {
  const schema = TOOL_SCHEMAS[toolName]
  if (!schema) {
    throw new Error(`Schema not found for tool: ${toolName}`)
  }

  if (schemaType === 'input') {
    return schema.input || {}
  } else if (schemaType === 'output') {
    return schema.output || {}
  } else {
    throw new Error(`Invalid schema type: ${schemaType}`)
  }
}

/**
 * Validate tool parameters against schema
 */
export function validateToolParameters(toolName, parameters) {
  const schema = getToolSchema(toolName, 'input')

  // Basic validation - in production, use a proper JSON schema validator like Ajv
  if (schema.required) {
    for (const field of schema.required) {
      if (!(field in parameters)) {
        throw new Error(`Missing required parameter: ${field}`)
      }
    }
  }

  // Type validation
  if (schema.properties) {
    for (const [field, fieldSchema] of Object.entries(schema.properties)) {
      if (field in parameters) {
        validateFieldType(field, parameters[field], fieldSchema)
      }
    }
  }

  return true
}

/**
 * Validate individual field type
 */
function validateFieldType(fieldName, value, schema) {
  const { type, minimum, maximum, minLength, maxLength, enum: enumValues } = schema

  if (type === 'string' && typeof value !== 'string') {
    throw new Error(`Parameter ${fieldName} must be a string`)
  }

  if (type === 'number' && typeof value !== 'number') {
    throw new Error(`Parameter ${fieldName} must be a number`)
  }

  if (type === 'integer' && !Number.isInteger(value)) {
    throw new Error(`Parameter ${fieldName} must be an integer`)
  }

  if (type === 'boolean' && typeof value !== 'boolean') {
    throw new Error(`Parameter ${fieldName} must be a boolean`)
  }

  if (type === 'array' && !Array.isArray(value)) {
    throw new Error(`Parameter ${fieldName} must be an array`)
  }

  if (type === 'object' && (typeof value !== 'object' || Array.isArray(value))) {
    throw new Error(`Parameter ${fieldName} must be an object`)
  }

  // Range validation
  if (typeof value === 'number') {
    if (minimum !== undefined && value < minimum) {
      throw new Error(`Parameter ${fieldName} must be at least ${minimum}`)
    }
    if (maximum !== undefined && value > maximum) {
      throw new Error(`Parameter ${fieldName} must be at most ${maximum}`)
    }
  }

  // String length validation
  if (typeof value === 'string') {
    if (minLength !== undefined && value.length < minLength) {
      throw new Error(`Parameter ${fieldName} must be at least ${minLength} characters`)
    }
    if (maxLength !== undefined && value.length > maxLength) {
      throw new Error(`Parameter ${fieldName} must be at most ${maxLength} characters`)
    }
  }

  // Enum validation
  if (enumValues && !enumValues.includes(value)) {
    throw new Error(`Parameter ${fieldName} must be one of: ${enumValues.join(', ')}`)
  }
}

/**
 * Get all available tool categories
 */
export function getToolCategories() {
  const categories = new Set()

  for (const [toolName, schema] of Object.entries(TOOL_SCHEMAS)) {
    // Extract category from tool name or use 'general' as default
    const parts = toolName.split('-')
    if (parts.length > 1) {
      categories.add(parts[0])
    } else {
      categories.add('general')
    }
  }

  return Array.from(categories).sort()
}

export default TOOL_SCHEMAS

/**
 * Global Xero Context for App-Wide Connection Management
 *
 * Provides centralized Xero connection state and OAuth flow management
 * across all components that need financial data.
 */

import { createContext, useContext, useState, useEffect, useCallback } from 'react'
import { logInfo, logWarn, logError } from '../utils/structuredLogger'

const XeroContext = createContext()

export const useXero = () => {
  const context = useContext(XeroContext)
  if (!context) {
    throw new Error('useXero must be used within a XeroProvider')
  }
  return context
}

export const XeroProvider = ({ children }) => {
  const [isConnected, setIsConnected] = useState(false)
  const [isLoading, setIsLoading] = useState(true)
  const [connectionStatus, setConnectionStatus] = useState(null)
  const [organizationInfo, setOrganizationInfo] = useState(null)
  const [lastError, setLastError] = useState(null)

  // Check connection status on mount and periodically
  const checkConnectionStatus = useCallback(async () => {
    try {
      setIsLoading(true)
      setLastError(null)

      logInfo('[XeroContext] Checking Xero connection status...')

      const response = await fetch('/api/xero/status')
      const data = await response.json()

      logInfo('[XeroContext] Xero status API response:', data)

      if (data.success && data.status) {
        const wasConnected = isConnected
        setIsConnected(data.status.connected)
        setConnectionStatus(data.status)

        // Log state changes
        if (wasConnected !== data.status.connected) {
          logInfo('[XeroContext] Connection state changed:', {
            from: wasConnected,
            to: data.status.connected,
          })
        }

        if (data.status.connected && data.status.organizationId) {
          setOrganizationInfo({
            id: data.status.organizationId,
            name: data.status.organizationName || 'Connected Organization',
            lastSync: data.status.lastSync,
            tokenExpiry: data.status.tokenExpiry,
          })
          logInfo('[XeroContext] Organization info updated:', {
            id: data.status.organizationId,
            name: data.status.organizationName,
          })
        } else {
          setOrganizationInfo(null)
        }

        logInfo('[XeroContext] Final state:', {
          connected: data.status.connected,
          hasTokens: data.status.hasTokens,
          organizationId: data.status.organizationId,
        })
      } else {
        logWarn('[XeroContext] Invalid API response, setting disconnected state')
        setIsConnected(false)
        setConnectionStatus(null)
        setOrganizationInfo(null)
      }
    } catch (error) {
      logError('[XeroContext] Failed to check Xero connection status', error)
      setLastError(error.message)
      setIsConnected(false)
    } finally {
      setIsLoading(false)
    }
  }, [isConnected])

  // Initialize connection check on mount
  useEffect(() => {
    logInfo('[XeroContext] Initializing XeroContext, checking connection status...')
    checkConnectionStatus()

    // Check connection status every 5 minutes
    const interval = setInterval(
      () => {
        logInfo('[XeroContext] Periodic connection check (5min interval)')
        checkConnectionStatus()
      },
      5 * 60 * 1000
    )

    return () => {
      logInfo('[XeroContext] Cleaning up XeroContext interval')
      clearInterval(interval)
    }
  }, [checkConnectionStatus])

  // OAuth callback handling removed - not needed for custom connection

  // OAuth flow removed - custom connection is always available when credentials are configured

  // Disconnect method removed - custom connections are managed via environment configuration

  // Retry connection (refresh status)
  const retry = useCallback(() => {
    setLastError(null)
    checkConnectionStatus()
  }, [checkConnectionStatus])

  const value = {
    // Connection state
    isConnected,
    isLoading,
    connectionStatus,
    organizationInfo,
    lastError,

    // Actions
    retry,
    checkConnectionStatus,
  }

  return <XeroContext.Provider value={value}>{children}</XeroContext.Provider>
}

import { useState, useEffect } from 'react'

const isDevelopmentMode = import.meta.env.VITE_DEVELOPMENT_MODE === 'true'

const useAuthRole = () => {
  const [authData, setAuthData] = useState({
    isAuthorized: isDevelopmentMode,
    role: isDevelopmentMode ? 'admin' : 'guest',
    loading: !isDevelopmentMode,
  })

  useEffect(() => {
    const loadAuth = async () => {
      if (isDevelopmentMode) {
        // Development mode - use mock data
        const { mockUser } = await import('../auth/MockUser.js')
        setAuthData({
          isAuthorized: true,
          role: mockUser.publicMetadata.role || 'admin',
          user: mockUser,
          loading: false,
        })
      } else {
        // Production mode - use real Clerk auth
        try {
          const { useAuth } = await import('@clerk/clerk-react')
          const { isSignedIn, user } = useAuth()
          setAuthData({
            isAuthorized: Boolean(isSignedIn),
            role: isSignedIn ? user?.publicMetadata?.role || 'member' : 'guest',
            user: user,
            loading: false,
          })
        } catch (error) {
          console.error('[useAuthRole] Failed to load Clerk auth:', error)
          setAuthData({
            isAuthorized: false,
            role: 'guest',
            loading: false,
          })
        }
      }
    }

    loadAuth()
  }, [])

  return authData
}

