function RunwayStatusBadge({ months }) {
  let status = 'good';
  let label = 'Healthy';
  let className = 'bg-green-100 text-green-800';

  if (months < 3) {
    status = 'critical';
    label = 'Critical';
    className = 'bg-red-100 text-red-800';
  } else if (months < 6) {
