function MetricBadge({ label, value }) {
  return (
    <div className="bg-white rounded-lg p-3 border border-green-200">
      <p className="text-xs text-gray-600 mb-1">{label}</p>
      <p className="text-lg font-bold text-gray-900">{value}</p>
