/**
 * Inventory Metric Card
 */
function InventoryMetricCard({ label, value, unit, trend, icon: Icon, format = 'number' }) {
  const StatusIcon = trend >= 0 ? TrendingUp : TrendingDown;
  const trendColor = trend >= 0 ? 'text-green-600' : 'text-red-600';

  const formattedValue = format === 'currency'
    ? `${value.toLocaleString()}`
    : format === 'percentage'
    ? `${value.toFixed(1)}%`
    : value.toLocaleString();

  return (
    <div className="bg-white rounded-lg shadow p-6">
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center gap-3">
          <div className="p-3 bg-blue-100 rounded-lg">
            <Icon className="w-6 h-6 text-blue-600" />
          </div>
          <div>
            <p className="text-sm font-medium text-gray-600">{label}</p>
          </div>
        </div>
      </div>

      <div className="flex items-baseline gap-2">
        <span className="text-3xl font-bold text-gray-900">{formattedValue}</span>
        {unit && <span className="text-sm text-gray-600">{unit}</span>}
      </div>

      {trend !== undefined && (
        <div className={`flex items-center gap-1 mt-2 ${trendColor}`}>
          <StatusIcon className="w-4 h-4" />
          <span className="text-sm font-medium">
            {Math.abs(trend).toFixed(1)}% vs last month
          </span>
        </div>
      )}
    </div>
  );
}

