/**
 * Top SKUs Table
 */
function TopSKUsTable({ skus, metric = 'value' }) {
  const [sortBy, setSortBy] = useState(metric);
  const [sortOrder, setSortOrder] = useState('desc');

  const sortedSKUs = [...skus].sort((a, b) => {
    const aVal = a[sortBy];
    const bVal = b[sortBy];
    return sortOrder === 'asc' ? aVal - bVal : bVal - aVal;
  });

  const handleSort = (column) => {
    if (sortBy === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(column);
      setSortOrder('desc');
    }
  };

  return (
    <div className="bg-white rounded-lg shadow overflow-hidden">
      <div className="p-6">
        <h3 className="text-lg font-semibold text-gray-900">Top Performing SKUs</h3>
      </div>

      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                SKU
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Product
              </th>
              <th
                onClick={() => handleSort('quantity')}
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
              >
                Quantity {sortBy === 'quantity' && (sortOrder === 'asc' ? '' : '')}
              </th>
              <th
                onClick={() => handleSort('value')}
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
              >
                Value {sortBy === 'value' && (sortOrder === 'asc' ? '' : '')}
              </th>
              <th
                onClick={() => handleSort('turnover')}
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
              >
                Turnover {sortBy === 'turnover' && (sortOrder === 'asc' ? '' : '')}
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Status
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {sortedSKUs.slice(0, 10).map((sku) => (
              <tr key={sku.sku} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="font-medium text-gray-900">{sku.sku}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-gray-900">{sku.name}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-gray-900">{sku.quantity.toLocaleString()}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-gray-900">{sku.value.toLocaleString()}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-gray-900">{sku.turnover.toFixed(1)}x</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={`px-2 py-1 text-xs font-medium rounded ${
                    sku.status === 'in_stock' ? 'bg-green-100 text-green-700' :
                    sku.status === 'low_stock' ? 'bg-yellow-100 text-yellow-700' :
                    'bg-red-100 text-red-700'
                  }`}>
                    {sku.status.replace('_', ' ')}
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

/**
 * Main Inventory Dashboard Component
 */
export default function InventoryDashboard() {
  const queryClient = useQueryClient();
  const [selectedWarehouse, setSelectedWarehouse] = useState('all');
  const [dateRange, setDateRange] = useState('month');

  // Fetch inventory data
  const { data, isLoading, error } = useQuery({
    queryKey: ['inventory', 'dashboard', selectedWarehouse, dateRange],
    queryFn: async () => {
      const params = new URLSearchParams({ warehouse: selectedWarehouse, dateRange });
      const response = await fetch(`/api/v1/inventory/dashboard?${params}`, {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch inventory data');
      const result = await response.json();
      return result.data;
    },
    refetchInterval: 60000, // Refetch every minute
  });

  // SSE for real-time inventory updates
  const { connected } = useSSE('inventory', {
    enabled: true,
    onMessage: (message) => {
      if (message.type === 'inventory:update' || message.type === 'inventory:alert') {
        queryClient.invalidateQueries(['inventory', 'dashboard']);
      }
    },
  });

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-96">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading inventory data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-lg p-6">
        <p className="text-red-800">Error loading inventory data: {error.message}</p>
      </div>
    );
  }

  const {
    overview = {},
    warehouses = [],
    abc = {},
    turnover = [],
    topSKUs = [],
  } = data || {};

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Inventory Dashboard</h1>
          <p className="text-gray-600 mt-1">
            Multi-warehouse stock management
            {connected && (
              <span className="ml-2 inline-flex items-center gap-1 text-green-600">
                <span className="w-2 h-2 bg-green-500 rounded-full" />
                Live
              </span>
            )}
          </p>
        </div>

        {/* Filters */}
        <div className="flex items-center gap-3">
          <select
            value={selectedWarehouse}
            onChange={(e) => setSelectedWarehouse(e.target.value)}
            className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
          >
            <option value="all">All Warehouses</option>
            {Object.values(WAREHOUSES).map(warehouse => (
              <option key={warehouse.id} value={warehouse.id}>
                {warehouse.name}
              </option>
            ))}
          </select>

          <select
            value={dateRange}
            onChange={(e) => setDateRange(e.target.value)}
            className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
          >
            <option value="week">This Week</option>
            <option value="month">This Month</option>
            <option value="quarter">This Quarter</option>
            <option value="year">This Year</option>
          </select>

          <button className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center gap-2">
            <Download className="w-4 h-4" />
            Export
          </button>
        </div>
      </div>

      {/* Metrics Cards */}
      <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6">
        <InventoryMetricCard
          label="Total Stock Value"
          value={overview.totalValue || 0}
          trend={overview.valueTrend}
          icon={DollarSign}
          format="currency"
        />

        <InventoryMetricCard
          label="Total Units"
          value={overview.totalUnits || 0}
          trend={overview.unitsTrend}
          icon={Package}
          format="number"
        />

        <InventoryMetricCard
          label="Avg Turnover Ratio"
          value={overview.avgTurnover || 0}
          unit="x per year"
          trend={overview.turnoverTrend}
          icon={Activity}
          format="number"
        />

        <InventoryMetricCard
          label="Days on Hand"
          value={overview.daysOnHand || 0}
          unit="days"
          trend={-overview.daysOnHandTrend}
          icon={BarChart3}
          format="number"
        />
      </div>

      {/* Warehouse Comparison */}
      <WarehouseComparisonChart warehouseData={warehouses} />

      {/* Charts Row */}
      <div className="grid md:grid-cols-2 gap-6">
        <ABCAnalysisChart abcData={abc} />
        <StockTurnoverChart turnoverData={turnover} />
      </div>

      {/* Top SKUs Table */}
      <TopSKUsTable skus={topSKUs} />
    </div>
  );
}

/**
 * Stock Alerts Component
 *
 * Real-time inventory alerts and actions:
 * - Low stock warnings
 * - Overstock alerts
 * - Expiration warnings
 * - Dead stock identification
 * - Quick action buttons (reorder, transfer, mark down)
 * - Alert history and trends
 */

import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import {
  AlertTriangle,
  AlertCircle,
  Clock,
  TrendingDown,
  Package,
  XCircle,
  CheckCircle2,
  ShoppingCart,
  RefreshCw,
  ArrowRight,
  Tag,
  X,
} from 'lucide-react';
import {
  ResponsiveContainer,
  BarChart,
  Bar,
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  PieChart,
  Pie,
  Cell,
} from 'recharts';
import { useSSE } from '../../hooks/useSSE';

// Alert types and severity
const ALERT_TYPES = {
  LOW_STOCK: { label: 'Low Stock', icon: AlertTriangle, color: 'orange' },
  OUT_OF_STOCK: { label: 'Out of Stock', icon: XCircle, color: 'red' },
  OVERSTOCK: { label: 'Overstock', icon: Package, color: 'yellow' },
  EXPIRING_SOON: { label: 'Expiring Soon', icon: Clock, color: 'orange' },
  DEAD_STOCK: { label: 'Dead Stock', icon: TrendingDown, color: 'gray' },
};

const ALERT_SEVERITY = {
  critical: { label: 'Critical', color: 'bg-red-500', textColor: 'text-red-600', borderColor: 'border-red-500' },
  high: { label: 'High', color: 'bg-orange-500', textColor: 'text-orange-600', borderColor: 'border-orange-500' },
  medium: { label: 'Medium', color: 'bg-yellow-500', textColor: 'text-yellow-600', borderColor: 'border-yellow-500' },
  low: { label: 'Low', color: 'bg-blue-500', textColor: 'text-blue-600', borderColor: 'border-blue-500' },
};

/**
 * Stock Alert Card
 */
function StockAlertCard({ alert, onAction, onDismiss }) {
  const alertType = ALERT_TYPES[alert.type] || ALERT_TYPES.LOW_STOCK;
  const severity = ALERT_SEVERITY[alert.severity] || ALERT_SEVERITY.medium;
  const Icon = alertType.icon;

  const [showActions, setShowActions] = useState(false);

  return (
    <div className={`rounded-lg border-2 ${severity.borderColor} bg-white p-4 hover:shadow-md transition-shadow`}>
      {/* Header */}
      <div className="flex items-start justify-between mb-3">
        <div className="flex items-center gap-3 flex-1">
          <div className={`p-2 rounded-lg ${severity.color} bg-opacity-10`}>
            <Icon className={`w-5 h-5 ${severity.textColor}`} />
          </div>
          <div className="flex-1">
            <div className="flex items-center gap-2 mb-1">
              <h4 className="font-semibold text-gray-900">{alert.sku}</h4>
              <span className={`px-2 py-0.5 rounded text-xs font-medium ${severity.color} text-white`}>
                {severity.label}
              </span>
            </div>
            <p className="text-sm text-gray-600">{alert.productName}</p>
          </div>
        </div>
        <button
          onClick={() => onDismiss(alert.id)}
          className="text-gray-400 hover:text-gray-600"
        >
          <X className="w-4 h-4" />
        </button>
      </div>

      {/* Alert Details */}
      <div className="space-y-2 mb-3">
        <div className="flex items-center justify-between text-sm">
          <span className="text-gray-600">Type:</span>
          <span className="font-medium text-gray-900">{alertType.label}</span>
        </div>

        {alert.currentStock !== undefined && (
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-600">Current Stock:</span>
            <span className={`font-medium ${
              alert.currentStock === 0 ? 'text-red-600' :
              alert.currentStock < alert.reorderPoint ? 'text-orange-600' :
              'text-gray-900'
            }`}>
              {alert.currentStock} units
            </span>
          </div>
        )}

        {alert.reorderPoint && (
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-600">Reorder Point:</span>
            <span className="font-medium text-gray-900">{alert.reorderPoint} units</span>
          </div>
        )}

        {alert.warehouse && (
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-600">Warehouse:</span>
            <span className="font-medium text-gray-900">{alert.warehouse}</span>
          </div>
        )}

        {alert.daysUntilExpiry && (
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-600">Days Until Expiry:</span>
            <span className={`font-medium ${
              alert.daysUntilExpiry <= 7 ? 'text-red-600' :
              alert.daysUntilExpiry <= 30 ? 'text-orange-600' :
              'text-gray-900'
            }`}>
              {alert.daysUntilExpiry} days
            </span>
          </div>
        )}

        {alert.daysStagnant && (
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-600">Days Stagnant:</span>
            <span className="font-medium text-gray-900">{alert.daysStagnant} days</span>
          </div>
        )}

        {alert.message && (
          <div className="mt-2 p-2 bg-gray-50 rounded text-sm text-gray-700">
            {alert.message}
          </div>
        )}
      </div>

      {/* Quick Actions */}
      <div className="border-t border-gray-200 pt-3">
        <button
          onClick={() => setShowActions(!showActions)}
          className="text-sm text-blue-600 hover:text-blue-700 font-medium flex items-center gap-1"
        >
          Quick Actions
          <ArrowRight className={`w-4 h-4 transition-transform ${showActions ? 'rotate-90' : ''}`} />
        </button>

        {showActions && (
          <div className="mt-3 grid grid-cols-2 gap-2">
            {alert.type === 'LOW_STOCK' || alert.type === 'OUT_OF_STOCK' ? (
              <>
                <button
                  onClick={() => onAction('reorder', alert)}
                  className="px-3 py-2 bg-blue-600 text-white rounded text-sm font-medium hover:bg-blue-700 flex items-center justify-center gap-1"
                >
                  <ShoppingCart className="w-4 h-4" />
                  Reorder
                </button>
                <button
                  onClick={() => onAction('transfer', alert)}
                  className="px-3 py-2 border border-gray-300 rounded text-sm font-medium text-gray-700 hover:bg-gray-50 flex items-center justify-center gap-1"
                >
                  <RefreshCw className="w-4 h-4" />
                  Transfer
                </button>
              </>
            ) : null}

            {alert.type === 'OVERSTOCK' || alert.type === 'DEAD_STOCK' ? (
              <>
                <button
                  onClick={() => onAction('markdown', alert)}
                  className="px-3 py-2 bg-orange-600 text-white rounded text-sm font-medium hover:bg-orange-700 flex items-center justify-center gap-1"
                >
                  <Tag className="w-4 h-4" />
                  Mark Down
                </button>
                <button
                  onClick={() => onAction('transfer', alert)}
                  className="px-3 py-2 border border-gray-300 rounded text-sm font-medium text-gray-700 hover:bg-gray-50 flex items-center justify-center gap-1"
                >
                  <RefreshCw className="w-4 h-4" />
                  Transfer
                </button>
              </>
            ) : null}

            {alert.type === 'EXPIRING_SOON' ? (
              <>
                <button
                  onClick={() => onAction('markdown', alert)}
                  className="px-3 py-2 bg-orange-600 text-white rounded text-sm font-medium hover:bg-orange-700 flex items-center justify-center gap-1"
                >
                  <Tag className="w-4 h-4" />
                  Mark Down
                </button>
                <button
                  onClick={() => onAction('transfer', alert)}
                  className="px-3 py-2 border border-gray-300 rounded text-sm font-medium text-gray-700 hover:bg-gray-50 flex items-center justify-center gap-1"
                >
                  <RefreshCw className="w-4 h-4" />
                  Transfer
                </button>
              </>
            ) : null}
          </div>
        )}
      </div>

      {/* Timestamp */}
      <div className="mt-3 text-xs text-gray-500">
        Alert created: {new Date(alert.createdAt).toLocaleString()}
      </div>
    </div>
  );
}

/**
 * Alert Summary Cards
 */
function AlertSummaryCards({ summary }) {
  const cards = [
