/**
 * Main Stock Alerts Component
 */
export default function StockAlerts() {
  const queryClient = useQueryClient();
  const [filterSeverity, setFilterSeverity] = useState('ALL');
  const [filterType, setFilterType] = useState('ALL');
  const [showDismissed, setShowDismissed] = useState(false);

  // Fetch alerts
  const { data, isLoading, error } = useQuery({
    queryKey: ['inventory', 'alerts', filterSeverity, filterType, showDismissed],
    queryFn: async () => {
      const params = new URLSearchParams({
        severity: filterSeverity,
        type: filterType,
        includeDismissed: showDismissed.toString(),
      });
      const response = await fetch(`/api/v1/inventory/alerts?${params}`, {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch alerts');
      const result = await response.json();
      return result.data;
    },
    refetchInterval: 30000,
  });

  // Dismiss alert mutation
  const dismissAlertMutation = useMutation({
    mutationFn: async (alertId) => {
      const response = await fetch(`/api/v1/inventory/alerts/${alertId}/dismiss`, {
        method: 'PATCH',
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to dismiss alert');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries(['inventory', 'alerts']);
    },
  });

  // Handle action mutation
  const handleActionMutation = useMutation({
    mutationFn: async ({ action, alert }) => {
      const response = await fetch(`/api/v1/inventory/alerts/${alert.id}/action`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ action, sku: alert.sku, warehouse: alert.warehouse }),
      });
      if (!response.ok) throw new Error(`Failed to execute ${action}`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries(['inventory', 'alerts']);
    },
  });

  // SSE for real-time alerts
  const { connected, lastMessage } = useSSE('inventory', {
    enabled: true,
    onMessage: (message) => {
      if (message.type === 'inventory:alert') {
        queryClient.invalidateQueries(['inventory', 'alerts']);
        // Show toast notification for new alert
        console.log('New inventory alert:', message.data);
      }
    },
  });

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-96">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading alerts...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-lg p-6">
        <p className="text-red-800">Error loading alerts: {error.message}</p>
      </div>
    );
  }

  const {
    alerts = [],
    summary = {},
    byType = {},
    trend = [],
  } = data || {};

  // Filter alerts
  const filteredAlerts = alerts.filter(alert => {
    const matchesSeverity = filterSeverity === 'ALL' || alert.severity === filterSeverity.toLowerCase();
    const matchesType = filterType === 'ALL' || alert.type === filterType;
    return matchesSeverity && matchesType;
  });

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Stock Alerts</h1>
          <p className="text-gray-600 mt-1">
            Real-time inventory alerts and actions
            {connected && (
              <span className="ml-2 inline-flex items-center gap-1 text-green-600">
                <span className="w-2 h-2 bg-green-500 rounded-full" />
                Live
              </span>
            )}
          </p>
        </div>

        {/* Filters */}
        <div className="flex items-center gap-3">
          <select
            value={filterSeverity}
            onChange={(e) => setFilterSeverity(e.target.value)}
            className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
          >
            <option value="ALL">All Severities</option>
            {Object.keys(ALERT_SEVERITY).map(severity => (
              <option key={severity} value={severity.toUpperCase()}>
                {ALERT_SEVERITY[severity].label}
              </option>
            ))}
          </select>

          <select
            value={filterType}
            onChange={(e) => setFilterType(e.target.value)}
            className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
          >
            <option value="ALL">All Types</option>
            {Object.entries(ALERT_TYPES).map(([key, type]) => (
              <option key={key} value={key}>
                {type.label}
              </option>
            ))}
          </select>

          <label className="flex items-center gap-2 text-sm text-gray-700">
            <input
              type="checkbox"
              checked={showDismissed}
              onChange={(e) => setShowDismissed(e.target.checked)}
              className="rounded"
            />
            Show Dismissed
          </label>
        </div>
      </div>

      {/* Summary Cards */}
      <AlertSummaryCards summary={summary} />

      {/* Charts */}
      <div className="grid md:grid-cols-2 gap-6">
        <AlertsByTypeChart alertsData={byType} />
        <AlertTrendChart trendData={trend} />
      </div>

      {/* Alerts List */}
      <div>
        <h2 className="text-xl font-semibold text-gray-900 mb-4">
          Active Alerts ({filteredAlerts.length})
        </h2>

        {filteredAlerts.length === 0 ? (
          <div className="bg-white rounded-lg shadow p-12 text-center">
            <CheckCircle2 className="w-16 h-16 text-green-500 mx-auto mb-4" />
            <h3 className="text-lg font-semibold text-gray-900 mb-2">No Active Alerts</h3>
            <p className="text-gray-600">
              {filterSeverity !== 'ALL' || filterType !== 'ALL'
                ? 'No alerts match the selected filters.'
                : 'All inventory levels are within acceptable ranges.'}
            </p>
          </div>
        ) : (
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
            {filteredAlerts.map((alert) => (
              <StockAlertCard
                key={alert.id}
                alert={alert}
                onAction={(action, alert) => handleActionMutation.mutate({ action, alert })}
                onDismiss={(id) => dismissAlertMutation.mutate(id)}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

/**
 * OEE (Overall Equipment Effectiveness) Dashboard Component
 *
 * Comprehensive OEE monitoring with formula breakdown:
 * - OEE = Availability  Performance  Quality
 * - Real-time metrics and trends
 * - Machine-by-machine breakdown
 * - Loss analysis (Six Big Losses)
 * - Shift comparison
 */

import { useState } from 'react';
import { useQuery, useQueryClient } from '@tanstack/react-query';
import {
  Activity,
  TrendingUp,
  TrendingDown,
  Minus,
  Clock,
  Zap,
  CheckCircle2,
  AlertTriangle,
  Target,
  BarChart3,
  Calendar,
  ChevronDown,
  ChevronUp,
} from 'lucide-react';
import {
  ResponsiveContainer,
  LineChart,
  Line,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ReferenceLine,
  ComposedChart,
  Area,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  Radar,
} from 'recharts';
import { useSSE } from '../../hooks/useSSE';

/**
 * OEE Metric Card Component
 */
function OEEMetricCard({ label, value, target, formula, breakdown, icon: Icon, color }) {
  const [showBreakdown, setShowBreakdown] = useState(false);

  const percentage = value;
  const targetPercentage = target;
  const difference = percentage - targetPercentage;
  const status = percentage >= targetPercentage ? 'good' : percentage >= targetPercentage - 5 ? 'warning' : 'poor';

  const statusColors = {
    good: 'border-green-500 bg-green-50',
    warning: 'border-yellow-500 bg-yellow-50',
    poor: 'border-red-500 bg-red-50',
  };

  const trendColors = {
    good: 'text-green-600',
    warning: 'text-yellow-600',
    poor: 'text-red-600',
  };

  return (
    <div className={`rounded-lg border-2 ${statusColors[status]} p-6 transition-all`}>
      {/* Header */}
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center gap-3">
          <div className={`p-3 rounded-lg ${color}`}>
            <Icon className="w-6 h-6 text-white" />
          </div>
          <div>
            <p className="text-sm font-medium text-gray-600">{label}</p>
            <p className="text-xs text-gray-500">{formula}</p>
          </div>
        </div>
        <button
          onClick={() => setShowBreakdown(!showBreakdown)}
          className="text-gray-400 hover:text-gray-600"
        >
          {showBreakdown ? <ChevronUp className="w-5 h-5" /> : <ChevronDown className="w-5 h-5" />}
        </button>
      </div>

      {/* Value */}
      <div className="mb-4">
        <div className="flex items-baseline gap-2">
          <span className="text-4xl font-bold text-gray-900">{percentage.toFixed(1)}%</span>
          <span className={`text-sm font-medium ${trendColors[status]}`}>
            {difference >= 0 ? '+' : ''}{difference.toFixed(1)}%
          </span>
        </div>
        <div className="flex items-center gap-2 mt-2">
          <span className="text-sm text-gray-600">Target: {targetPercentage}%</span>
          {difference >= 0 ? (
            <TrendingUp className="w-4 h-4 text-green-600" />
          ) : (
            <TrendingDown className="w-4 h-4 text-red-600" />
          )}
        </div>
      </div>

      {/* Progress Bar */}
      <div className="relative w-full bg-gray-200 rounded-full h-3 mb-4">
        <div
          className={`h-3 rounded-full transition-all ${
            status === 'good' ? 'bg-green-500' :
            status === 'warning' ? 'bg-yellow-500' :
            'bg-red-500'
          }`}
          style={{ width: `${Math.min(percentage, 100)}%` }}
        />
        <div
          className="absolute top-0 h-3 w-0.5 bg-gray-800"
          style={{ left: `${targetPercentage}%` }}
        />
      </div>

      {/* Breakdown */}
      {showBreakdown && breakdown && (
        <div className="mt-4 pt-4 border-t border-gray-200 space-y-2">
          {Object.entries(breakdown).map(([key, val]) => (
            <div key={key} className="flex items-center justify-between text-sm">
              <span className="text-gray-600">{key}</span>
              <span className="font-medium text-gray-900">{val}</span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

/**
 * Machine OEE Table
 */
function MachineOEETable({ machines }) {
  const [sortBy, setSortBy] = useState('oee');
  const [sortOrder, setSortOrder] = useState('desc');

  const sortedMachines = [...machines].sort((a, b) => {
    const aVal = a[sortBy];
    const bVal = b[sortBy];
    return sortOrder === 'asc' ? aVal - bVal : bVal - aVal;
  });

  const handleSort = (column) => {
    if (sortBy === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(column);
      setSortOrder('desc');
    }
  };

  const getStatusBadge = (value, target) => {
    const status = value >= target ? 'good' : value >= target - 5 ? 'warning' : 'poor';
    const colors = {
      good: 'bg-green-100 text-green-700 border-green-300',
      warning: 'bg-yellow-100 text-yellow-700 border-yellow-300',
      poor: 'bg-red-100 text-red-700 border-red-300',
    };
    return (
      <span className={`px-2 py-1 rounded text-xs font-medium border ${colors[status]}`}>
        {value.toFixed(1)}%
      </span>
    );
  };

  return (
    <div className="bg-white rounded-lg shadow overflow-hidden">
      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Machine
              </th>
              <th
                onClick={() => handleSort('oee')}
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
              >
                OEE {sortBy === 'oee' && (sortOrder === 'asc' ? '' : '')}
              </th>
              <th
                onClick={() => handleSort('availability')}
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
              >
                Availability {sortBy === 'availability' && (sortOrder === 'asc' ? '' : '')}
              </th>
              <th
                onClick={() => handleSort('performance')}
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
              >
                Performance {sortBy === 'performance' && (sortOrder === 'asc' ? '' : '')}
              </th>
              <th
                onClick={() => handleSort('quality')}
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
              >
                Quality {sortBy === 'quality' && (sortOrder === 'asc' ? '' : '')}
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Status
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {sortedMachines.map((machine) => (
              <tr key={machine.id} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="flex items-center">
                    <div className="font-medium text-gray-900">{machine.name}</div>
                  </div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  {getStatusBadge(machine.oee, 85)}
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  {getStatusBadge(machine.availability, 90)}
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  {getStatusBadge(machine.performance, 95)}
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  {getStatusBadge(machine.quality, 99)}
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={`px-2 py-1 rounded text-xs font-medium ${
                    machine.status === 'running' ? 'bg-green-100 text-green-700' :
                    machine.status === 'idle' ? 'bg-yellow-100 text-yellow-700' :
                    'bg-red-100 text-red-700'
                  }`}>
                    {machine.status}
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

/**
 * Six Big Losses Analysis
 */
function SixBigLossesChart({ lossData }) {
  const losses = [
    { category: 'Breakdowns', value: lossData.breakdowns || 0, color: '#ef4444' },
    { category: 'Setup/Adjustments', value: lossData.setup || 0, color: '#f97316' },
    { category: 'Small Stops', value: lossData.smallStops || 0, color: '#f59e0b' },
    { category: 'Reduced Speed', value: lossData.reducedSpeed || 0, color: '#eab308' },
    { category: 'Startup Rejects', value: lossData.startupRejects || 0, color: '#84cc16' },
    { category: 'Production Rejects', value: lossData.productionRejects || 0, color: '#22c55e' },
  ];

  const totalLoss = losses.reduce((sum, loss) => sum + loss.value, 0);

  return (
    <div className="bg-white rounded-lg shadow p-6">
      <h3 className="text-lg font-semibold text-gray-900 mb-4">
        Six Big Losses Analysis
      </h3>

      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={losses} layout="vertical">
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis type="number" domain={[0, 100]} tickFormatter={(v) => `${v}%`} />
          <YAxis type="category" dataKey="category" width={150} />
          <Tooltip formatter={(value) => `${value.toFixed(1)}%`} />
          <Bar dataKey="value" fill="#3b82f6">
            {losses.map((entry, index) => (
              <Bar key={`cell-${index}`} fill={entry.color} />
            ))}
          </Bar>
        </BarChart>
      </ResponsiveContainer>

      <div className="mt-4 grid grid-cols-2 md:grid-cols-3 gap-4">
        {losses.map((loss) => (
          <div key={loss.category} className="flex items-center gap-2">
            <div
              className="w-3 h-3 rounded-full"
              style={{ backgroundColor: loss.color }}
            />
            <div className="flex-1">
              <p className="text-xs text-gray-600">{loss.category}</p>
              <p className="text-sm font-medium text-gray-900">
                {loss.value.toFixed(1)}%
              </p>
            </div>
          </div>
        ))}
      </div>

      <div className="mt-4 pt-4 border-t border-gray-200">
        <div className="flex items-center justify-between">
          <span className="text-sm font-medium text-gray-700">Total Loss</span>
          <span className="text-lg font-bold text-red-600">{totalLoss.toFixed(1)}%</span>
        </div>
      </div>
    </div>
  );
}

