  if (!mcpClient) {
    mcpClient = new MCPClient()
  }
  return mcpClient
}

/**
 * Initialize MCP client with custom config
 */
export function initializeMCPClient(config = {}) {
  mcpClient = new MCPClient(config)
  return mcpClient
}

