  pdf.setFontSize(12)
  pdf.setFont(undefined, 'normal')
  pdf.setTextColor(100, 100, 100)
  pdf.text('Enterprise Manufacturing Intelligence', margin + 35, margin + 16)

  // Horizontal line
  pdf.setDrawColor(200, 200, 200)
